INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53972, 'Diclofenac', 'Lomefloxacin', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54043/', '[1] "Product Information. Factive (gemifloxacin)." GeneSoft Inc, San Francisco, CA.[2] "Product Information. Avelox (moxifloxacin)" Bayer, West Haven, CT.[3] Davey PG "Overview of drug interactions with the quinolones." J Antimicrob Chemother 22(suppl c) (1988): 97-107[4] Domagala JM "Structure-activity and structure-side-effect relationships for the quinolone antibacterials." J Antimicrob Chemother 33 (1994): 685-706[5] "Product Information. Cipro (ciprofloxacin)." Bayer, West Haven, CT.[6] "Product Information. Floxin (ofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.[7] "Product Information. Tequin (gatifloxacin)" Bristol-Myers Squibb, Princeton, NJ.[8] Segev S. Rehavi M, Rubinstein E "Quinolones, theophylline, and diclofenac interactions with the gamma-aminobutyric acid receptor." Antimicrob Agents Chemother 32 (1988): 1624-6[9] Hooper DC, Wolfson JS "The fluoroquinolones: pharmacology, clinical uses, and toxicities in humans." Antimicrob Agents Chemother 28 (1985): 716-21[10] Ball P "Ciprofloxacin: an overview of adverse experiences." J Antimicrob Chemother 18 (1986): 187-93[11] "Product Information. Penetrex (enoxacin)." Rhone-Poulenc Rorer, Collegeville, PA.[12] "Product Information. Raxar (grepafloxacin)." Glaxo Wellcome, Research Triangle Park, NC.[13] Ball P, Tillotson G "Tolerability of fluoroquinolone antibiotics: past, present and future." Drug Saf 13 (1996): 343-8[14] "Product Information. Levaquin (levofloxacin)." Ortho Pharmaceutical Corporation, Raritan, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53973, 'Diclofenac', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54044/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53974, 'Diclofenac', 'Lorlatinib', 'Moderate', 'Coadministration with lorlatinib may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C9 isoenzyme. The proposed mechanism involves decreased metabolic clearance due to induction of CYP450 2C9 by lorlatinib.', NULL, 'Caution is advised when lorlatinib is used concurrently with drugs that are known CYP450 2C9 substrates, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised when lorlatinib is used concurrently with drugs that are known CYP450 2C9 substrates, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lorlatinib is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54045/', '[1] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[5] "Product Information. Lorbrena (lorlatinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[8] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[9] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53975, 'Diclofenac', 'Losartan', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of angiotensin II receptor antagonists. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and angiotensin II receptor antagonists may cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving angiotensin II receptor antagonists who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54046/', '[1] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[2] "Multum Information Services, Inc. Expert Review Panel"[3] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[5] "Product Information. Diovan (valsartan)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Cozaar (losartan)." Merck & Co, Inc, West Point, PA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53976, 'Diclofenac', 'Macimorelin', 'Moderate', 'Drugs that directly affect the pituitary secretion of growth hormone (e.g., somatostatin, insulin, glucocorticoids, cyclooxygenase inhibitors such as aspirin and nonsteroidal anti-inflammatory drugs) and drugs that may transiently elevate growth hormone levels (e.g., clonidine, levodopa, insulin) may impact the accuracy of the macimorelin diagnostic test.', NULL, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided.', NULL, 'Concomitant use of macimorelin with drugs affecting growth hormone levels should generally be avoided. A sufficient washout period following discontinuation of these drugs is recommended prior to macimorelin administration.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54047/', '[1] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.[2] "Product Information. Macrilen (macimorelin)." Aeterna Zentaris, Charleston, SC.', 1767365204);
