INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53985, 'Diclofenac', 'Miconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 2C9 may increase the plasma concentrations of diclofenac, which is primarily metabolized by the isoenzyme.', NULL, 'Caution is advised if diclofenac is prescribed in combination with an inhibitor of CYP450 2C9.', NULL, 'Caution is advised if diclofenac is prescribed in combination with an inhibitor of CYP450 2C9.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54056/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Arthrotec (diclofenac-misoprostol)." Searle, Skokie, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53986, 'Diclofenac', 'Mifepristone', 'Moderate', 'Coadministration with mifepristone may increase the plasma concentrations of drugs that are substrates of CYP450 2C8 and/or 2C9. The mechanism is decreased clearance due to inhibition of CYP450 2C8/2C9 activity by mifepristone.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that are substrates of CYP450 2C8 and/or 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if mifepristone must be used concomitantly with medications that are substrates of CYP450 2C8 and/or 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever mifepristone is added to or withdrawn from therapy. The lowest dosage of sensitive substrates (e.g., repaglinide, warfarin) should be used whenever possible. Because mifepristone is eliminated slowly from the body, drug interactions may be observed for a prolonged period following discontinuation (approximately 2 to 3 weeks if mifepristone had been administered chronically to steady state).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54057/', '[1] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[2] "Product Information. Korlym (mifepristone)." Corcept Therapeutics Incorporated, Menlo Park, CA.[3] "Product Information. Mifeprex (mifepristone)" Danco Laboratories, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (53987, 'Diclofenac', 'Milnacipran', 'Moderate', 'Serotonin reuptake inhibitors (SRIs) may potentiate the risk of bleeding in patients treated with ulcerogenic agents and agents that affect hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The tricyclic antidepressant, clomipramine, is also a strong SRI and may interact similarly. Serotonin release by platelets plays an important role in hemostasis, thus SRIs may alter platelet function and induce bleeding.', NULL, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if SRIs or clomipramine are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54058/', '[1] Bannister SJ, Houser VP, Hulse JD, Kisicki JC, Rasmussen JG "Evaluation of the potential for interactions of paroxetine with diazepam, cimetidine, warfarin, and digoxin." Acta Psychiatr Scand Suppl 350 (1989): 102-6[2] Skop BP, Brown TM "Potential vascular and bleeding complications of treatment with selective serotonin reuptake inh