    ) VALUES (1356223, 'Benzphetamine', 'Ascorbic acid', 'Moderate', 'Gastrointestinal acidifying agents such as glutamic acid HCl and ascorbic acid may lower the blood levels and efficacy of amphetamines.', NULL, 'Patients receiving an amphetamine in combination with gastrointestinal acidifying agents should be monitored for potentially diminished pharmacologic response to the amphetamine.', NULL, 'Patients receiving an amphetamine in combination with gastrointestinal acidifying agents should be monitored for potentially diminished pharmacologic response to the amphetamine.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
