INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54022, 'Diclofenac', 'Pentazocine', 'Moderate', 'Grand mal seizures following a single diclofenac dose have been reported in a patient on long-term pentazocine therapy. Seizures recurred upon inadvertent rechallenge with diclofenac. The role pentazocine played in this case remains to be determined. However, pentazocine is known to reduce the seizure threshold.', NULL, 'Patients receiving this combination should be monitored for increased CNS stimulation (tremors, involuntary muscle movements, seizures) and should be advised to notify their physician if they experience such symptoms.', NULL, 'Patients receiving this combination should be monitored for increased CNS stimulation (tremors, involuntary muscle movements, seizures) and should be advised to notify their physician if they experience such symptoms.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54093/', '[1] Heim M, Nadvorna H, Azaria M "Grand mal seizures following treatment with diclofenac and pentazocine." S Afr Med J 78 (1990): 700-1[2] D''Arcy PF "Tobacco smoking and drugs: a clinically important interaction?" Drug Intell Clin Pharm 18 (1984): 302-7[3] "Product Information. Talacen (acetaminophen-pentazocine)." Sanofi-Synthelabo Inc, New York, NY.[4] Miller LG "Recent developments in the study of the effects of cigarette smoking on clinical pharmacokinetics and clinical pharmacodynamics." Clin Pharmacokinet 17 (1989): 90-108', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54023, 'Diclofenac', 'Pentoxifylline', 'Moderate', 'The use of pentoxifylline has been associated with bleeding and/or prolongation in prothrombin time. Theoretically, the risk of bleeding may be increased by concomitant treatment with anticoagulants or other agents that affect hemostasis such as nonsteroidal anti-inflammatory drugs (NSAIDs), platelet inhibitors, thrombin inhibitors, thrombolytic agents, and dextran. There have been postmarketing reports of increased anticoagulant activity in patients receiving vitamin K antagonists in combination with pentoxifylline.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis.', NULL, 'Caution is advised if pentoxifylline is administered with anticoagulants or other agents that affect hemostasis. Patients on vitamin K antagonists such as warfarin should have more frequent monitoring of INR following initiation or dosage change of pentoxifylline, while patients receiving other agents that affect hemostasis should have periodic examinations for bleeding including hematocrit and/or hemoglobin.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54094/', '[1] "Product Information. Trental (pentoxifylline)." Hoechst Marion-Roussel Inc, Kansas City, MO.[2] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54024, 'Diclofenac', 'Perindopril', 'Moderate', 'Nonsteroidal anti-inflammatory drugs (NSAIDs) may attenuate the antihypertensive effects of ACE inhibitors. The proposed mechanism is NSAID-induced inhibition of renal prostaglandin synthesis, which results in unopposed pressor activity producing hypertension. In addition, NSAIDs can cause fluid retention, which also affects blood pressure. Concomitant use of NSAIDs and ACE inhibitors may also cause deterioration in renal function, particularly in patients who are elderly or volume-depleted (including those on diuretic therapy) or have compromised renal function. Acute renal failure may occur, although effects are usually reversible. Chronic use of NSAIDs alone may be associated with renal toxicities, including elevations in serum creatinine and BUN, tubular necrosis, glomerulitis, renal papillary necrosis, acute interstitial nephritis, nephrotic syndrome, and renal failure.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID.', NULL, 'Patients receiving ACE inhibitors who require prolonged (greater than 1 week) concomitant therapy with an NSAID should have blood pressure monitored more closely following initiation, discontinuation, or change of dosage of the NSAID. Renal function should also be evaluated periodically during prolonged coadministration. The interaction is not expected to occur with low doses (e.g., low-dose aspirin) or intermittent short-term administration of NSAIDs.', 'Antagonism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54095/', '[1] Townend JN, Doran J, Lote CJ, Davies MK "Peripheral haemodynamic effects of inhibition of prostaglandin synthesis in congestive heart failure and interactions with captopril." Br Heart J 73 (1995): 434-41[2] Ahmad S "Indomethacin-enalapril interaction: an alert." South Med J 84 (1991): 411-2[3] Silberbauer K, Stanek B, Templ H "Acute hypotensive effect of captopril in man modified by prostaglandin synthesis inhibition." Br J Clin Pharmacol 14 (1982): s87-93[4] "Product Information. Toradol (ketorolac)." Roche Laboratories, Nutley, NJ.[5] Morgan T, Anderson A "Interaction of indomethacin with felodipine and enalapril." J Hypertens 11 (1993): S338-9[6] Radack KL, Deck CC, Bloomfield SS "Ibuprofen interferes with the efficacy of antihypertensive drugs: a randomized, double-blind, placebo-controlled trial of ibuprofen compared with acetaminophen." Ann Intern Med 107 (1987): 628-35[7] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.[8] Abdel-Haq B, Magagna A, Favilla S, Salvetti A "Hemodynamic and humoral interactions between perindopril and indomethacin in essential hypertensive subjects." J Cardiovasc Pharmacol 18 (1991): s33-6[9] "Product Information. Daypro (oxaprozin)." Searle, Skokie, IL.[10] Polonia J, Boaventura I, Gama G, Camoes I, Bernardo F, Andrade P, Nunes JP, Brandao F, Cerqueiragomes M "Influence of non-steroidal anti-inflammatory drugs on renal function and 24h ambulatory blood pressure-reducing effects of enalapril and nifedipine gastrointestinal therapeutic system in hypertensive patients." J Hypertens 13 (1995): 925-31[11] Seto S, Aoi W, Iwami K, et al "Effect of propranolol and indomethacin on the depressor action of captopril in patients with essential hypertension." Clin Exp Hypertens 9 (1987): 623-7[12] Allon M, Pasque CB, Rodriguez M "Interaction of captopril and ibuprofen on glomerular and tubular function in humans." Am J Physiol 259 (1990): f233-8[13] Moore TJ, Crantz FR, Hollenberg NK "Contribution of prostaglandins to the antihypertensive action of captopril in essential hypertension." Hypertension 3 (1981): 168-73[14] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[15] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[16] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54025, 'Diclofenac', 'Pexidartinib', 'Major', 'Serious cases of hepatotoxicity, some fatal, have occurred in patients treated with pexidartinib. Concomitant use of other potentially hepatotoxic agents may potentiate the risk of liver injury. The mechanism of hepatotoxicity is unknown, its occurrence cannot be predicted, and it is unknown whether liver injury occurs in the absence of increased transaminases.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided.', NULL, 'The use of pexidartinib with other potentially hepatotoxic agents should be avoided. Patients treated with pexidartinib should have liver function tests, including AST, ALT, total bilirubin, direct bilirubin, ALP, and gamma-glutamyl transferase (GGT), prior to initiation of pexidartinib, weekly for the first 8 weeks, every 2 weeks for the next month, and every 3 months thereafter. Pexidartinib therapy may require a dosage reduction, to be withheld, or permanently discontinued based on the severity of the hepatotoxicity. A recurrence of increased serum transaminases, bilirubin, or ALP may occur upon rechallenge with a reduced dose of pexidartinib. Liver function tests should be performed weekly for the first month after rechallenge. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54096/', '[1] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.[2] "Product Information. Turalio (pexidartinib)." Daiichi Sankyo, Inc., Parsippany, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54026, 'Diclofenac', 'Phenobarbital', 'Minor', 'Phenobarbital toxicity has been reported in a patient following initiation of diclofenac. The mechanism and clinical significance of this possible interaction is unknown. If these two drugs must be coadministered, the patient should be observed closely for phenobarbital toxicity such as slowness, ataxia, nystagmus, reduced reflexes and coma. Phenobarbital serum concentrations may need to be more closely monitored.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54097/', '[1] "Product Information. Arthrotec (diclofenac-misoprostol)." Searle, Skokie, IL.', 1767365204);
