INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54058, 'Diclofenac', 'Reteplase', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54129/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54059, 'Human Rho(D) immune globulin', 'Diclofenac', 'Major', 'Coadministration of intravenous immune globulin preparations with nephrotoxic agents may potentiate the risk of renal impairment. Many commercially available intravenous formulations of immune globulin contain sucrose as a stabilizer.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.', NULL, 'Intravenous immune globulin preparations should be administered cautiously in patients treated with other potentially nephrotoxic agents (e.g., e.g., aminoglycosides; polypeptide, glycopeptide, and polymyxin antibiotics; amphotericin B; adefovir; cidofovir; tenofovir; foscarnet; cisplatin; deferasirox; gallium nitrate; lithium; mesalamine; certain immunosuppressants; intravenous bisphosphonates; intravenous pentamidine; high intravenous dosages of methotrexate; high dosages and/or chronic use of nonsteroidal anti-inflammatory agents). Clinicians should ensure that patients are not volume depleted prior to the initiation of immune globulin therapy. Monitoring of urine output and renal function tests, including the measurement of blood urea nitrogen (BUN) and serum creatinine, is recommended prior to the initial infusion and at appropriate intervals thereafter.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54130/', '[1] MMWR Morb Mortal Wkly Rep "Renal insufficiency and failure associated with immune globulin intravenous therapy -- United States, 1985-1998. Available from: URL: http://www.cdc.gov/mmwr/preview/mmwrhtml/mm4824a3.htm." ([1999 Jun 25]):[2] "Product Information. BabyBIG (botulism immune globulin)." FFF Enterprises, Temecula, CA.[3] Guo X, Nzerue C "How to prevent, recognize, and treat drug-induced nephrotoxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2002 Apr]):[4] Moses S "Nephrotoxic drugs, drug-induced nephrotoxicity. Available from: URL: http://www.fpnotebook.com/Renal/Pharm/NphrtxcDrgs.htm." ([2011 Nov 25]):[5] Perazella MA "Renal vulnerability to drug toxicity. Available from: URL: http://www.ccjm.org/content/69/4/289.full.pdf." ([2009]):[6] Levy JB, Pusey EY "Nephrotoxicity of intravenous immunoglobulin. Available from: URL: http://qjmed.oxfordjournals.org/content/93/11/751.full.pdf+html." ([2000]):[7] "Product Information. Respigam (respiratory syncytial virus immune globulin)." Medimmune Inc, Gaithersburg, MD.[8] "Product Information. Bivigam (immune globulin intravenous)." Biotest Pharmaceuticals Corporation, Boca Raton, FL.[9] "Product Information. Cytogam (cytomegalovirus immune globulin)." CSL Behring, King of Prussia, PA.[10] Naughton CA "Drug-induced nephrotoxicity. Available from: URL: http://www.aafp.org/afp/2008/0915/p743.html." ([2008]):[11] KDIGO. Kidney Disease Improving Global Outcomes "KDIGO clinical practice guideline for acute kidney injury. Available from: URL: http://www.kdigo.org/clinical_practice_guidelines/pdf/KDIGO-AKI-Suppl-Appendices-A-F_March2012.pdf." ([2012 Mar]):', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54060, 'Diclofenac', 'Rifampicin', 'Moderate', 'Coadministration with CYP450 2C9 inducers, such as rifampin, may decrease the plasma concentrations and therapeutic effects of diclofenac. The proposed mechanism is rifampin-mediated induction of CYP450 2C9, the isoenzyme responsible for the metabolic clearance of diclofenac.', NULL, 'The potential for diminished pharmacologic effects of diclofenac should be considered during coadministration with CYP450 2C9 inducers, such as rifampin.', NULL, 'The potential for diminished pharmacologic effects of diclofenac should be considered during coadministration with CYP450 2C9 inducers, such as rifampin. Pharmacologic response to diclofenac should be monitored and the diclofenac dosage adjusted as necessary whenever a CYP450 2C9 inducer is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54131/', '[1] "Product Information. Voltaren (diclofenac)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Kumar JS, Rao Mamidi NVS, Chakrapani T "Rifampicin pretreatment reduces bioavailability of diclofenac sodium." Indian J Pharmacol 27 (1995): 183-5[3] Cerner Multum, Inc. "Australian Product Information." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54061, 'Diclofenac', 'Rifapentine', 'Moderate', 'Coadministration with rifapentine may decrease the plasma concentrations of drugs that are substrates of the CYP450 2C8, 2C9, and/or 3A4 isoenzymes. The mechanism is accelerated clearance due to induction of these isoenzymes by rifapentine. Enzyme activities may be induced within 4 days of the first dose and return to normal 14 days after discontinuation of rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine.', NULL, 'The possibility of a diminished therapeutic response to drugs that are known substrates of CYP450 2C8, 2C9, and/or 3A4 should be considered during coadministration with rifapentine. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs, particularly those with a narrow therapeutic range, whenever rifapentine is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54132/', '[1] Thijssen HH, Flin ois JP, Beaune PH "Cytochrome P4502C9 is the principal catalyst of racemic acenocoumarol hydroxylation reactions in human liver microsomes." Drug Metab Dispos 28 (2000): 1284-90[2] "Product Information. Priftin (rifapentine)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54062, 'Diclofenac', 'Ritonavir', 'Minor', 'Ritonavir has a moderate affinity for the CYP450 2C9 microsomal enzyme and may variably affect drugs that are substrates of this enzyme. The area under the plasma concentration-time curves for these drugs may increase or decrease. If concomitant therapy is necessary, close monitoring for therapeutic and adverse effects is strongly recommended.', NULL, '-', NULL, '-', 'Metabolism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54133/', '[1] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.[2] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Kaletra (lopinavir-ritonavir)" Abbott Pharmaceutical, Abbott Park, IL.[4] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.[5] "Product Information. Norvir (ritonavir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54063, 'Diclofenac', 'Rivaroxaban', 'Major', 'Concomitant use of rivaroxaban with other agents that alter hemostasis such as aspirin, nonsteroidal anti-inflammatory drugs (NSAIDs), platelet aggregation inhibitors, other anticoagulants, thrombolytic agents, or drugs that cause thrombocytopenia may increase the risk of bleeding. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during rivaroxaban therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term neurological injury or permanent paralysis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis.', NULL, 'Caution is recommended if rivaroxaban must be used with other agents that alter hemostasis. Patients should be monitored for increased anticoagulant effects and bleeding complications. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits. Besides bleeding complications, patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54134/', '[1] "Product Information. Xarelto (rivaroxaban)." Bayer Inc, Toronto, IA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54064, 'Diclofenac', 'Rofecoxib', 'Moderate', 'Concomitant use of COX-2 inhibitors with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal toxicity including inflammation, bleeding, ulceration, and perforation. The risk is dependent on both dosage and duration of therapy.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided.', NULL, 'The use of COX-2 inhibitors with non-aspirin NSAIDs should be avoided. Some authorities consider the use of COX-2 inhibitors with non-aspirin NSAIDs as contraindicated.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54135/', '[1] Cerner Multum, Inc "ANVISA Bulrio Eletrnico. Available from: URL: http://www.anvisa.gov.br/datavisa/fila_bula/index.asp." O 0 (2015):[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Cerner Multum, Inc. "Australian Product Information." O 0[4] "Product Information. Celebrex (celecoxib)." Searle, Chicago, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54065, 'Diclofenac', 'Rucaparib', 'Moderate', 'Coadministration with rucaparib may increase the plasma concentrations and the risk of adverse effects of drugs that are substrates of CYP450 2C9. The proposed mechanism is decreased clearance due to rucaparib-mediated inhibition of CYP450 2C9.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range.', NULL, 'Caution is advised if rucaparib is used concomitantly with drugs that are substrates of CYP450 2C9, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever rucaparib is added to or withdrawn from therapy with these drugs. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54136/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] "Product Information. Accolate (zafirlukast)." Zeneca Pharmaceuticals, Wilmington, DE.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54066, 'Diclofenac', 'Rufinamide', 'Moderate', 'Rufinamide may modestly or moderately decrease the plasma concentrations and therapeutic effects of drugs that are CYP450 3A4 substrates. The mechanism is enhanced metabolism by CYP450 3A4 enzyme induction.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose.', NULL, 'During concomitant treatment with rufinamide and drugs that are CYP450 3A4 substrates, it is recommended that patients should be monitored for 2 weeks when rufinamide is added to or withdrawn from therapy, or after increases of the dose. Dose adjustments of the coadministered drug may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54137/', '[1] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Yamreudeewong W, Henann NE, Fazio A, Lower DL, Cassidy TG "Drug-food interactions in clinical practice." J Fam Pract 40 (1995): 376-84[4] "Product Information. Ambien (zolpidem)." sanofi-aventis, Bridgewater, NJ.[5] "Product Information. Banzel (rufinamide)." Eisai Inc, Teaneck, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54067, 'Diclofenac', 'Ruxolitinib', 'Moderate', 'Systemically and topically administered nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of bleeding in patients treated with anticoagulants and other drugs that affect hemostasis such as platelet inhibitors, thrombin inhibitors, thrombolytic agents, or agents that commonly cause thrombocytopenia. The pharmacologic effects of NSAIDs that contribute to this interaction include prolongation of prothrombin time and inhibition of platelet adhesion and aggregation.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis.', NULL, 'Caution is advised if NSAIDs are used in combination with other drugs that affect hemostasis. Close clinical and laboratory observation for hematologic complications is recommended. Patients should be advised to promptly report any signs of bleeding to their doctor, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54138/', '[1] "Product Information. Nevanac (nepafenac ophthalmic)." Alcon Laboratories Inc, Fort Worth, TX.[2] "Product Information. Acular (ketorolac)." Allergan Inc, Irvine, CA.[3] "Product Information. Xibrom (bromfenac ophthalmic)." ISTA Pharmaceuticals, Irvine, CA.', 1767365204);
