INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54779, 'Carbamazepine', 'Cannabidiol', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 and/or CYP450 2C19 may decrease the plasma concentrations of cannabidiol. The proposed mechanism of action is induction of CYP450 3A4 and/or 2C19-mediated metabolism of cannabidiol.', NULL, 'A dosage increase of cannabidiol up to 2-fold may be required when coadministered with potent inducers of CYP450 3A4 and/or CYP450 2C19 based on clinical response and tolerability.', NULL, 'A dosage increase of cannabidiol up to 2-fold may be required when coadministered with potent inducers of CYP450 3A4 and/or CYP450 2C19 based on clinical response and tolerability.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/54850/', '[1] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[4] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.', 1767365204);
