INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54948, 'Cannabidiol', 'Lomitapide', 'Major', 'Coadministration of lomitapide with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Lomitapide can cause elevations in serum transaminases and hepatic steatosis. Clinical data suggest that hepatic fat accumulation is reversible after stopping treatment with lomitapide, although the long-term consequences are unknown.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.', NULL, 'Caution is advised if lomitapide is used with other potentially hepatotoxic agents (e.g., acetaminophen; alcohol; amiodarone; androgens and anabolic steroids; antituberculous agents; azole antifungal agents; ACE inhibitors; cyclosporine (high dosages); disulfiram; endothelin receptor antagonists; interferons; ketolide and macrolide antibiotics; kinase inhibitors; methotrexate; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; tamoxifen; tetracyclines; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; other lipid-lowering medications such as fenofibrate, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice). Patients treated with lomitapide should have serum ALT, AST, alkaline phosphatase, and total bilirubin measured prior to initiation of treatment and regularly during treatment in accordance with the product labeling, and the dosing adjusted or interrupted as necessary. Since alcohol may increase levels of hepatic fat and induce or exacerbate liver injury, the manufacturer recommends that patients taking lomitapide not consume more than one alcoholic drink per day. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55019/', '[1] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.[2] "Product Information. Juxtapid (lomitapide)." Aegerion Pharmaceuticals Inc, Cambridge, MA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (54949, 'Cannabidiol', 'Lomustine', 'Moderate', 'Coadministration of cannabidiol with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Cannabidiol causes dose-related elevations of liver transaminases, both alanine aminotransferase (ALT) and aspartate aminotransferase (AST).', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa.', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa. Serum transaminases and total bilirubin levels should be obtained prior to initiating cannabidiol, and patients with elevated baseline transaminase levels above 3 times the ULN accompanied by elevations in bilirubin above 2 times the ULN should be evaluated. Patients who develop clinical signs or symptoms suggestive of hepatic dysfunction (e.g., unexplained nausea, vomiting, right upper quadrant abdominal pain, fatigue, anorexia, jaundice, dark urine) should have serum transaminases and total bilirubin measured promptly, and cannabidiol treatment interrupted or discontinued as appropriate. Cannabidiol should be discontinued in patients with elevations of transaminase levels greater than 3 times the ULN and bilirubin levels greater than 2 times the ULN. Patients with sustained transaminase elevations of greater than 5 times the ULN should also have treatment discontinued. Patients with prolonged elevations of serum transaminases should be evaluated for other possible causes. Also consider dosage adjustment or discontinuation of any coadministered medication that is known to affect the liver.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55020/', '[1] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.', 1767365204);
