INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55138, 'Cannabidiol', 'Temozolomide', 'Moderate', 'Coadministration of cannabidiol with other agents known to induce hepatotoxicity may potentiate the risk of liver injury. Cannabidiol causes dose-related elevations of liver transaminases, both alanine aminotransferase (ALT) and aspartate aminotransferase (AST).', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa.', NULL, 'Caution is advised if cannabidiol is used in patients who are currently receiving or have recently received treatment with other hepatotoxic agents, and vice versa. Serum transaminases and total bilirubin levels should be obtained prior to initiating cannabidiol, and patients with elevated baseline transaminase levels above 3 times the ULN accompanied by elevations in bilirubin above 2 times the ULN should be evaluated. Patients who develop clinical signs or symptoms suggestive of hepatic dysfunction (e.g., unexplained nausea, vomiting, right upper quadrant abdominal pain, fatigue, anorexia, jaundice, dark urine) should have serum transaminases and total bilirubin measured promptly, and cannabidiol treatment interrupted or discontinued as appropriate. Cannabidiol should be discontinued in patients with elevations of transaminase levels greater than 3 times the ULN and bilirubin levels greater than 2 times the ULN. Patients with sustained transaminase elevations of greater than 5 times the ULN should also have treatment discontinued. Patients with prolonged elevations of serum transaminases should be evaluated for other possible causes. Also consider dosage adjustment or discontinuation of any coadministered medication that is known to affect the liver.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55209/', '[1] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55139, 'Cannabidiol', 'Temsirolimus', 'Moderate', 'Concomitant use of cannabidiol with calcineurin or mammalian target of rapamycin (mTOR) inhibitors may increase the serum levels of the calcineurin or mTOR inhibitor and the risk of adverse effects. The mechanism for this interaction is not clearly understood.', NULL, 'Although the interaction has not been studied, caution is advised if cannabidiol is coadministered with calcineurin or mTOR inhibitors.', NULL, 'Although the interaction has not been studied, caution is advised if cannabidiol is coadministered with calcineurin or mTOR inhibitors. Clinical and laboratory monitoring should be considered whenever cannabidiol is added to or withdrawn from therapy with a calcineurin or mTOR inhibitor, and the dosage of the calcineurin or mTOR inhibitor adjusted as necessary. Patients should be monitored for development of adverse effects associated with the calcineurin or mTOR inhibitor.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55210/', '[1] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.[2] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] "Product Information. Epidiolex (cannabidiol)." Greenwich Biosciences Inc, Carlsbad, CA.', 1767365204);
