INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55401, 'Inotersen', 'Caplacizumab', 'Major', 'Coadministration of inotersen and drugs that interfere with platelet function or coagulation may potentiate the risk of serious, potentially life-threatening bleeding complications, including spontaneous intracranial and intrapulmonary hemorrhage. Inotersen causes reductions in platelet count that may result in sudden and unpredictable thrombocytopenia.', NULL, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding.', NULL, 'Caution is advised when inotersen is prescribed with antiplatelet agents, anticoagulants, or other medications that commonly cause thrombocytopenia or bleeding. A platelet count should be obtained prior to initiation of inotersen and regularly during and for at least 8 weeks after treatment in accordance with the product labeling. Inotersen should not be administered in patients with a platelet count below 100 x 10^9/L or in patients who are unable to adhere to the recommended laboratory monitoring and management guidelines. Patients or their caregivers should be apprised of the signs and symptoms of thrombocytopenia and to seek medical attention if they occur, including any unusual or prolonged bleeding (e.g., petechiae, easy bruising, hematoma, subconjunctival bleeding, gingival bleeding, epistaxis, hemoptysis, irregular or heavier than normal menstrual bleeding, hematemesis, hematuria, hematochezia, melena), neck stiffness, or atypical severe headache.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55472/', '[1] "Product Information. Tegsedi (inotersen)." Akcea Therapeutics, Cambridge, MA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Brukinsa (zanubrutinib)." BeiGene USA, Inc, San Mateo, CA.', 1767365204);
