INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55461, 'Amprenavir', 'Capmatinib', 'Major', 'Coadministration of capmatinib with strong CYP450 3A4 inhibitors may increase the risk and severity of capmatinib adverse effects, such as interstitial lung disease, pneumonitis, and hepatotoxicity. The proposed mechanism is decreased clearance due to inhibition of CYP450 3A4, which is one of the primary enzymes responsible for the metabolic clearance of capmatinib.', NULL, 'Close monitoring is recommended whenever capmatinib is used with a strong CYP450 3A4 inhibitor.', NULL, 'Close monitoring is recommended whenever capmatinib is used with a strong CYP450 3A4 inhibitor. Clinical and laboratory monitoring should be considered whenever a strong CYP450 3A4 inhibitor is added to or withdrawn from therapy with capmatinib, and the dosage adjusted as necessary based on clinical response and toxicity. Patients should be monitored for the development of adverse effects.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55532/', '[1] "Product Information. Tabrecta (capmatinib)." Novartis Pharmaceuticals, East Hanover, NJ.[2] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67[3] "Product Information. VFEND (voriconazole)." Pfizer U.S. Pharmaceuticals, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55462, 'Capmatinib', 'Artesunate', 'Moderate', 'Coadministration with capmatinib may increase the plasma concentrations and risk of adverse effects of drugs that are substrates of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) transporters, such as digoxin and rosuvastatin. The proposed mechanism is decreased clearance due to capmatinib-mediated inhibition of intestinal P-gp and/or BCRP efflux transport proteins.', NULL, 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided.', NULL, 'Coadministration of capmatinib with drugs that are substrates of P-gp and/or BCRP should generally be avoided. However, if concomitant use is unavoidable, caution is advised, particularly with drugs that have a narrow therapeutic range. Clinical and laboratory monitoring should be considered whenever capmatinib is added to or withdrawn from therapy with these drugs, and dosages adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55533/', '[1] "Product Information. Tabrecta (capmatinib)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55463, 'Capmatinib', 'Bedaquiline', 'Moderate', 'Coadministration of bedaquiline with other agents known to induce hepatotoxicity may potentiate the risk of liver injury.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.', NULL, 'The use of bedaquiline with other potentially hepatotoxic agents should be avoided whenever possible (e.g., acetaminophen; alcohol; androgens and anabolic steroids; other antituberculous agents; azole antifungal agents; ACE inhibitors; disulfiram; endothelin receptor antagonists; ketolide and macrolide antibiotics; interferons; kinase inhibitors; minocycline; nonsteroidal anti-inflammatory agents; nucleoside reverse transcriptase inhibitors; proteasome inhibitors; retinoids; sulfonamides; tamoxifen; thiazolidinediones; tolvaptan; vincristine; zileuton; anticonvulsants such as carbamazepine, hydantoins, felbamate, and valproic acid; lipid-lowering medications such as fenofibrate, lomitapide, mipomersen, niacin, and statins; herbals and nutritional supplements such as black cohosh, chaparral, comfrey, DHEA, kava, pennyroyal oil, and red yeast rice), especially in patients with diminished hepatic reserve. Patients treated with bedaquiline should have serum ALT, AST, alkaline phosphatase, and bilirubin monitored at baseline and monthly during treatment, or as often as needed. An increase of serum aminotransferases to greater than 3 times ULN should be followed by repeat testing within 48 hours. Patients should be advised to seek medical attention if they experience potential signs and symptoms of hepatotoxicity such as fever, rash, itching, anorexia, nausea, vomiting, fatigue, malaise, right upper quadrant pain, dark urine, pale stools, and jaundice. Discontinue bedaquiline if aminotransferase elevations are accompanied by total bilirubin elevation greater than 2 times ULN, aminotransferase elevations are greater than 8 times ULN, or aminotransferase elevations persist beyond 2 weeks.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55534/', '[1] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[2] "Product Information. Sirturo (bedaquiline)." Janssen Pharmaceuticals, Titusville, NJ.[3] "Product Information. Hivid (zalcitabine)." Roche Laboratories, Nutley, NJ.', 1767365204);
