INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55480, 'Cobimetinib', 'Capmatinib', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 and/or P-glycoprotein (P-gp) may increase the plasma concentrations of cobimetinib, which is a substrate of both the isoenzyme and the efflux transporter.', NULL, 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors.', NULL, 'Caution is advised when cobimetinib is prescribed with CYP450 3A4 and/or P-gp inhibitors. Patients should be monitored for adverse effects such as diarrhea, nausea, vomiting, stomatitis, hemorrhage, cardiomyopathy, rash, photosensitivity, retinopathy, retinal vein occlusion, liver enzyme abnormalities and rhabdomyolysis, and the cobimetinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55551/', '[1] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[2] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[5] "Product Information. Mavyret (glecaprevir-pibrentasvir)." Abbott Pharmaceutical, Abbott Park, IL.', 1767365204);
