INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55581, 'Capmatinib', 'Ozanimod', 'Major', 'Coadministration with breast cancer resistance protein (BCRP) inhibitors may increase the exposure of the active metabolites of ozanimod and the risk of adverse effects. Coadministration with cyclosporine, a BCRP inhibitor, did not alter the AUC of ozanimod, but it did double the exposure of the minor active metabolites of ozanimod, RP101988 and RP101075 which are direct precursors of the major active metabolite CC112273.', NULL, 'According to the manufacturer, coadministration of ozanimod with BCRP inhibitors is not recommended.', NULL, 'According to the manufacturer, coadministration of ozanimod with BCRP inhibitors is not recommended.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55652/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.[3] "Product Information. Zeposia (ozanimod)." Celgene Corporation, Summit, NJ.', 1767365204);
