INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55649, 'Capmatinib', 'Tolvaptan', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) may increase the oral bioavailability of tolvaptan, which has been shown in vitro to be a substrate of the efflux transporter.', NULL, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine.', NULL, 'Caution is advised if tolvaptan is prescribed in combination with P-glycoprotein inhibitors, especially potent ones such as amiodarone, cyclosporine, or quinidine. Serum sodium concentration and neurologic status should be closely monitored, and the tolvaptan dosage adjusted accordingly. Too rapid correction of hyponatremia increases the risk of osmotic demyelination syndrome, which is associated with dysarthria, mutism, dysphagia, lethargy, affective changes, spastic quadriparesis, seizures, coma, and death. Discontinuation or interruption of tolvaptan therapy should be considered in patients who develop too rapid a rise in serum sodium.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/55720/', '[1] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.[2] "Product Information. Venclexta (venetoclax)." AbbVie US LLC, North Chicago, IL.[3] "Product Information. Samsca (tolvaptan)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
