INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (55954, 'Captopril', 'Potassium acetate', 'Major', 'Concomitant use of angiotensin converting enzyme (ACE) inhibitors and potassium salts may increase the risk of hyperkalemia. Inhibition of ACE results in decreased aldosterone secretion, which in turn causes potassium retention.', NULL, 'Caution is advised if ACE inhibitors must be used concurrently with potassium salts, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, trimethoprim, and licorice.', NULL, 'Caution is advised if ACE inhibitors must be used concurrently with potassium salts, particularly in patients with renal impairment, diabetes, old age, severe or worsening heart failure, dehydration, or concomitant therapy with other agents that increase serum potassium such as nonsteroidal anti-inflammatory drugs, beta-blockers, cyclosporine, heparin, tacrolimus, trimethoprim, and licorice. The combination should generally be avoided in these patients unless absolutely necessary and the benefits outweigh the potential risks. Serum potassium and renal function should be checked prior to initiating therapy and regularly thereafter. Patients should be given counseling on the appropriate levels of potassium and fluid intake, and advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', NULL, NUL