    ) VALUES (1357439, 'Dipyridamole', 'Cangrelor', 'Major', 'Concomitant use of cangrelor with anticoagulants, thrombolytic agents, or other antiplatelet agents may theoretically increase the risk of bleeding.', NULL, 'Caution is recommended if cangrelor must be used in patients treated with other agents that alter hemostasis.', NULL, 'Caution is recommended if cangrelor must be used in patients treated with other agents that alter hemostasis. Patients should be monitored for potentially increased bleeding complications.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
