INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357459, 'Dalteparin', 'Acetylsalicylic acid', 'Major', 'In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during low molecular weight heparin (LMWH) or heparinoid therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs). The development of epidural and spinal hematoma can lead to long-term or permanent paralysis. Theoretically, NSAIDs may potentiate the risk of bleeding complications associated with LMWH or heparinoid therapy. NSAIDs interfere with platelet adhesion and aggregation and may prolong bleeding time in healthy individuals. While these effects are generally slight and of relatively short duration with most NSAIDs (except aspirin) at recommended dosages, they may be of pronounced clinical significance when combined with the inhibitory effects of LMWHs or heparinoids on the clotting cascade.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids.', NULL, 'Products containing NSAIDs, especially if given chronically and in high dosages, should preferably be avoided in patients receiving LMWHs or heparinoids. Close clinical and laboratory observation for bleeding complications is recommended if concurrent therapy is necessary. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with caution and only after thorough assessment of risks and benefits.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357460, 'Dipyridamole', 'Danaparoid', 'Major', 'Drugs that can affect hemostasis such as dextran, platelet inhibitors, thrombin inhibitors, thrombolytic agents, or other anticoagulants may potentiate the risk of bleeding complications associated with the use of a low molecular weight heparin (LMWH), heparinoid, or fondaparinux. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during LMWH, heparinoid, or fondaparinux therapy may also be increased by the concomitant use of other drugs that affect coagulation. The development of epidural and spinal hematoma can lead to long-term or permanent paralysis.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy.', NULL, 'In general, any agent that can enhance the risk of hemorrhage including other anticoagulants should be discontinued prior to initiation of LMWH, heparinoid, or fondaparinux therapy. If coadministration is necessary, it should be undertaken with caution and only after thorough assessment of risks and benefits. Close clinical and laboratory observation for bleeding complications is recommended. Patients undergoing neuraxial intervention and treated with these agents should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction. The optimal timing between the administration of anticoagulants and neuraxial procedures is not known.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357461, 'Dipyridamole', 'Dasatinib', 'Major', 'Coadministration of dasatinib and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications. Treatment with dasatinib is associated with severe thrombocytopenia.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib.', NULL, 'Concomitant use of other medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with dasatinib. Close clinical and laboratory observation for bleeding complications is recommended during therapy. A complete blood count (CBC) with differential and platelet count should be obtained prior to and at least weekly for the first 2 months, then monthly thereafter or as clinically indicated. Patients should be advised to promptly report any signs of bleeding to their physician, including pain, swelling, headache, dizziness, weakness, prolonged bleeding from cuts, increased menstrual flow, vaginal bleeding, nosebleeds, bleeding of gums from brushing, unusual bleeding or bruising, red or brown urine, or red or black stools.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357462, 'Dipyridamole', 'Deferasirox', 'Major', 'Coadministration with drugs that have known ulcerogenic or hemorrhagic potential may increase the risk of gastrointestinal bleeding associated with the use of deferasirox. Fatal gastrointestinal hemorrhages have been reported during deferasirox therapy, especially in elderly patients with advanced hematologic malignancies and/or low platelet counts.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia.', NULL, 'Caution is advised if deferasirox is prescribed with drugs that have known ulcerogenic or hemorrhagic potential, including but not limited to NSAIDs, salicylates, corticosteroids, oral bisphosphonates, platelet inhibitors, anticoagulants, thrombolytic agents, or agents that commonly cause thrombocytopenia. Patients should be advised to contact their physician if they develop potential signs and symptoms of gastrointestinal injury such as abdominal pain, bloating, dizziness, lightheadedness, hematemesis, anorexia, and/or black, tarry stools.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357463, 'Defibrotide', 'Acetylsalicylic acid', 'Major', 'Coadministration of defibrotide with antiplatelet agents or other agents that affect coagulation may increase the risk of bleeding. In vitro, defibrotide increased the activity of fibrinolytic enzymes. In a mouse model of thromboembolism, recombinant tissue plasminogen activator (t-PA) potentiated the antithrombotic effect of defibrotide when given intravenously.', NULL, 'Concomitant use of medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with defibrotide.', NULL, 'Concomitant use of medications that interfere with platelet function or coagulation should be considered cautiously in patients treated with defibrotide. Close clinical and laboratory observation for bleeding complications is recommended during therapy. If bleeding develops, defibrotide should be discontinued and treatment/supportive care provided until the bleeding stops.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357464, 'Deflazacort', 'Acetylsalicylic acid', 'Moderate', 'Coadministration with corticosteroids may decrease the serum concentrations and therapeutic effects of salicylates. Likewise, serum salicylate levels may increase following withdrawal of corticosteroid therapy, potentially resulting in salicylate toxicity. Pharmacologically, the potential for increased gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration and perforation, should be considered due to additive ulcerogenic effects of these agents (especially aspirin) on the GI mucosa.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs.', NULL, 'Patients treated concomitantly with a corticosteroid may require higher dosages of salicylates or salicylate-like drugs. Pharmacologic response to these agents should be monitored more closely whenever a corticosteroid is added to or withdrawn from therapy in patients stabilized on their existing salicylate regimen, and the salicylate dosage adjusted as necessary. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as severe abdominal pain, dizziness, lightheadedness, and the appearance of black, tarry stools. The selective use of prophylactic anti-ulcer therapy (e.g., antacids, H2-antagonists) may be appropriate, particularly in patients with a prior history of peptic ulcer disease or GI bleeding and in elderly and debilitated patients.', 'Excretion', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357465, 'Delafloxacin', 'Acetylsalicylic acid', 'Moderate', 'Coadministration with nonsteroidal anti-inflammatory drugs (NSAIDs) may potentiate the risk of central nervous system toxicity sometimes associated with fluoroquinolone use. Some investigators suggest that the piperazine ring of fluoroquinolones may inhibit the binding of gamma-aminobutyric acid (GABA) to brain receptors and that NSAIDs may synergistically add to this effect. Patients with a history of seizures may be at greater risk.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', NULL, 'Clinical monitoring for signs of CNS stimulation such as tremors, involuntary muscle movements, hallucinations, or seizures is recommended if fluoroquinolone antibiotics are prescribed in combination with NSAIDs.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357466, 'Deoxycholic acid', 'Acetylsalicylic acid', 'Moderate', 'Coadministration with agents that alter hemostasis such as anticoagulants, platelet inhibitors, thrombin inhibitors, thrombolytics, or agents that commonly cause thrombocytopenia may increase the risk of submental hematoma or bruising in association with deoxycholic acid administration.', NULL, 'Deoxycholic acid should be used with caution in patients receiving drugs that may interfere with hemostasis.', NULL, 'Deoxycholic acid should be used with caution in patients receiving drugs that may interfere with hemostasis.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
INSERT OR IGNORE INTO drug_interactions (
        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357467, 'Desirudin', 'Acetylsalicylic acid', 'Major', 'Coadministration of thrombin inhibitors with drugs that affect platelet function may potentiate the risk of bleeding due to additive or synergistic effects on hemostasis. In patients receiving neuraxial anesthesia or spinal puncture, the risk of developing an epidural or spinal hematoma during thrombin inhibitor therapy may be increased by the concomitant use of other drugs that affect coagulation, including nonsteroidal anti-inflammatory drugs (NSAIDs) and platelet inhibitors.', NULL, 'Close clinical and laboratory observation for bleeding complications is recommended if thrombin inhibitors are used with drugs that interfere with platelet function, including long-term or chronic NSAIDs.', NULL, 'Close clinical and laboratory observation for bleeding complications is recommended if thrombin inhibitors are used with drugs that interfere with platelet function, including long-term or chronic NSAIDs. In patients undergoing neuraxial intervention, coadministration of these agents should be approached with extreme caution and only after thorough assessment of risks and benefits. Besides bleeding complications, these patients should also be monitored frequently for signs and symptoms of neurologic impairment such as midline back pain, sensory and motor deficits (numbness or weakness in lower limbs), and bowel or bladder dysfunction. Urgent diagnosis and treatment (e.g., spinal cord decompression) are required if signs or symptoms of spinal hematoma develop.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
