        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357484, 'Doxorubicin', 'Dipyridamole', 'Minor', 'Limited data have shown that dipyridamole can potentiate the cytotoxicity of doxorubicin. The synergy between dipyridamole and doxorubicin appears to be due to enhancement of intracellular doxorubicin accumulation by dipyridamole.', NULL, '-', NULL, '-', 'Synergism', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
