    ) VALUES (1357507, 'Fludarabine', 'Dipyridamole', 'Moderate', 'The concomitant administration of dipyridamole may theoretically reduce the therapeutic efficacy of fludarabine. Dipyridamole is an adenosine uptake inhibitor and may inhibit the cellular uptake of adenine analogs like fludarabine.', NULL, 'Although clinical data are lacking, it may be advisable to avoid this combination.', NULL, 'Although clinical data are lacking, it may be advisable to avoid this combination.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
