        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357528, 'Ibritumomab tiuxetan', 'Acetylsalicylic acid', 'Major', 'Coadministration of ibritumomab and drugs that interfere with platelet function or coagulation may potentiate the risk of bleeding complications.', NULL, 'Caution is advised if ibritumomab is used in combination with drugs that interfere with platelet function or coagulation.', NULL, 'Caution is advised if ibritumomab is used in combination with drugs that interfere with platelet function or coagulation. Close clinical and laboratory observation for bleeding complications is recommended during and after ibritumomab therapy.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
