        id, ingredient1, ingredient2, severity, effect, arabic_effect,
        recommendation, arabic_recommendation, management_text, mechanism_text,
        alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, updated_at
    ) VALUES (1357563, 'Acetylsalicylic acid', 'Lithium carbonate', 'Minor', 'Most studies have shown that aspirin and other salicylates do not significantly affect the serum concentrations of lithium. In general, no particular precaution should be necessary when lithium is coadministered with salicylates.', NULL, '-', NULL, '-', 'Others', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', 1767317475);
