INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56002, 'Captopril', 'Tacrolimus', 'Moderate', 'Coadministration of tacrolimus with ACE inhibitors or angiotensin II receptor antagonists may increase the risk of hyperkalemia associated with the use of these drugs, particularly in patients with renal impairment.', NULL, 'Serum potassium levels and renal function should be monitored regularly when tacrolimus is used in combination with ACE inhibitors or angiotensin II receptor antagonists.', NULL, 'Serum potassium levels and renal function should be monitored regularly when tacrolimus is used in combination with ACE inhibitors or angiotensin II receptor antagonists. Particular caution is warranted in patients with renal impairment, diabetes, old age, severe or worsening heart failure, or dehydration. Patients should be advised to seek medical attention if they experience signs and symptoms of hyperkalemia such as nausea, vomiting, weakness, listlessness, tingling of the extremities, paralysis, confusion, weak pulse, and a slow or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56074/', '[1] Perazella MA "Drug-induced hyperkalemia: old culprits and new offenders." Am J Med 109 (2000): 307-14[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Hooks MA "Tacrolimus, a new immunosuppressant--a review of the literature." Ann Pharmacother 28 (1994): 501-11[4] "Product Information. Prograf (tacrolimus)." Fujisawa, Deerfield, IL.[5] Ray K, Dorman S, Watson R "Severe hyperkalaemia due to the concomitant use of salt substitutes and ACE inhibitors in hypertension: a potentially life threatening interaction." J Hum Hypertens 13 (1999): 717-20[6] "Product Information. Vasotec (enalapril)." Merck & Co, Inc, West Point, PA.[7] Good CB, McDermott L "Diet and serum potassium in patients on ACE inhibitors." JAMA 274 (1995): 538', 1767365204);
