INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56004, 'Captopril', 'Talazoparib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and/or breast cancer resistance protein (BCRP) may increase the plasma concentrations of talazoparib, which has been shown in vitro to be a substrate of both efflux membrane transporters.', NULL, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil.', NULL, 'No initial dosage adjustment is recommended by the manufacturer when talazoparib is coadministered with inhibitors of BCRP and/or P-gp other than amiodarone, carvedilol, clarithromycin, itraconazole, or verapamil. However, patients should be closely monitored for adverse effects such as myelosuppression and myelodysplastic syndrome/acute myeloid leukemia, and dosage adjustments made or treatment withheld as needed in accordance with the product labeling.', 'Distribution', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56076/', '[1] "Product Information. Talzenna (talazoparib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365204);
