INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56154, 'Carbamazepine', 'Caspofungin', 'Major', 'Coadministration with inducers or mixed inducer/inhibitors of certain metabolic pathways may produce clinically meaningful reductions in the plasma concentrations of caspofungin. This assumption is based on results from regression analyses of patient pharmacokinetic data derived from a small number of patients who were administered the drugs efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone, or carbamazepine prior to and/or concurrently with caspofungin. There are no data from formal drug interaction studies to evaluate these regression analyses, and it is not known which metabolic pathway involved in caspofungin clearance may be inducible.', NULL, 'The manufacturer of caspofungin states that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', NULL, 'The manufacturer of caspofungin states that during coadministration with efavirenz, nelfinavir, nevirapine, phenytoin, rifampin, dexamethasone or carbamazepine, an increase in the daily dosage to 70 mg (following the usual 70 mg loading dose) should be considered in patients who are not clinically responding.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56226/', '[1] "Product Information. Cancidas (caspofungin)" Merck & Co, Inc, West Point, PA.', 1767365204);
