INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56184, 'Clozapine', 'Carbamazepine', 'Major', 'The use of clozapine with other potentially myelotoxic agents such as carbamazepine may increase the risk and/or severity of hematologic toxicity. Clozapine alone is associated with a significant risk of agranulocytosis, defined as an absolute neutrophil count (ANC) of less than 500/mm3. Case reports also suggest that carbamazepine may decrease the pharmacologic effects of clozapine. The mechanism may involve induction of CYP450 hepatic metabolism by carbamazepine. Increased serum clozapine levels have been reported after discontinuation of carbamazepine therapy.', NULL, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', NULL, 'The use of clozapine in combination with other agents having a well-known potential to cause agranulocytosis or otherwise suppress bone marrow function is considered contraindicated.', 'Metabolism, Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56256/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Clozaril (clozapine)." Novartis Pharmaceuticals, East Hanover, NJ.[4] Jerling M, Lindstrom L, Bondesson U, Bertilsson L "Fluvoxamine inhibition and carbamazepine induction of the metabolism of clozapine - evidence from a therapeutic drug monitoring service." Ther Drug Monit 16 (1994): 368-74[5] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[6] Raitasuo V, Lehtovaara R, Huttunen MO "Carbamazepine and plasma levels of clozapine." Am J Psychiatry 150 (1993): 169[7] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[8] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[9] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[10] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[11] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[12] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[13] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56185, 'Cobimetinib', 'Carbamazepine', 'Major', 'Coadministration with potent and moderate inducers of CYP450 3A4 may significantly decrease the plasma concentrations of cobimetinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of cobimetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of cobimetinib with potent or moderate CYP450 3A4 inducers should generally be avoided.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56257/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.[5] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[8] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[9] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[10] "Product Information. Cotellic (cobimetinib)." Genentech, South San Francisco, CA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56186, 'Codeine', 'Carbamazepine', 'Moderate', 'Coadministration of codeine with potent or moderate CYP450 3A4 inducers may result in lower codeine plasma concentrations, higher levels of the inactive metabolite norcodeine, and less metabolism via CYP450 2D6, resulting in lower morphine levels. This interaction may lead to reduced codeine efficacy and potentially initiate the onset of withdrawal symptoms in patients who are physically dependent. In addition, patients may be at an increased risk of CNS and/or respiratory-depressant effects from increased levels of codeine once concomitant therapy with the CYP450 3A4 inducer is ceased.', NULL, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer.', NULL, 'The potential loss of efficacy of codeine or dihydrocodeine and onset of opioid withdrawal symptoms should be considered when used in combination with a potent or moderate CYP450 3A4 inducer. Alternative agents with no or minimal CYP450 3A4 induction potential are recommended whenever possible. If concomitant use is considered necessary, caution and close clinical and laboratory monitoring are recommended. Dosage adjustments may also be required whenever a CYP450 3A4 inducer is added to or withdrawn from therapy. Following discontinuation of a CYP450 3A4 inducer, patients should be monitored for potentially excessive or prolonged CNS and respiratory depression.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56258/', '[1] Cerner Multum, Inc. "Austra