INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56206, 'Dasatinib', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of dasatinib, which is primarily metabolized by the isoenzyme.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of dasatinib with potent CYP450 3A4 inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered whenever possible during treatment with dasatinib. If coadministration is required, a dosage increase for dasatinib may be necessary depending on patient tolerability. Close monitoring for toxicities (e.g., myelosuppression, bleeding complications, fluid retention, QT prolongation) is recommended if the dosage of dasatinib is increased. The dosage should be reduced to the indicated dosage following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56278/', '[1] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[5] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[6] "Product Information. Sprycel (dasatinib)." Bristol-Myers Squibb, Princeton, NJ.[7] Cerner Multum, Inc. "Australian Product Information." O 0[8] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[9] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[10] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[11] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365204);
