INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56222, 'Dihydrotachysterol', 'Carbamazepine', 'Moderate', 'Coadministration with CYP450 inducers such as rifampin, isoniazid, barbiturates, and certain anticonvulsants may decrease the pharmacologic effects of vitamin D analogs. These agents are thought to induce the hepatic conversion of vitamin D to inactive metabolites and have been shown to reduce circulating levels of active vitamin D, sometimes accompanied by reduced serum calcium and increased parathyroid hormone levels. Patients on long-term anticonvulsant therapy have occasionally developed osteomalacia, presumably due to interference with vitamin D and calcium metabolism. There have also been isolated reports of patients who responded poorly to vitamin D supplements during treatment with phenytoin and/or primidone.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects.', NULL, 'Patients receiving vitamin D analogs with CYP450 inducers should be monitored for potentially reduced vitamin D effects. Dosage adjustments may be necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56294/', '[1] "Product Information. Rocaltrol (calcitriol)." Roche Laboratories, Nutley, NJ.[2] "Product Information. Zemplar (paricalcitol)." Abbott Pharmaceutical, Abbott Park, IL.[3] "Product Information. Hectorol (doxercalciferol)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. One-Alpha (alfacalcidol)." Pharmel Inc, Montreal, IN.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56223, 'Diltiazem', 'Carbamazepine', 'Major', 'Coadministration with diltiazem or verapamil may significantly increase the plasma concentrations of carbamazepine. The proposed mechanism is inhibition of carbamazepine metabolism via CYP450 3A4. There have been case reports of carbamazepine toxicity associated with the use of both calcium channel blockers (CCBs), which is consistent with their status as moderate CYP450 3A4 inhibitors. The onset is usually within 2 to 4 days after initiation of the CCB, and a reduction in carbamazepine dosage by 50% to 60% or discontinuation of the CCB is often required.', NULL, 'Caution is advised during concomitant use of carbamazepine with diltiazem or verapamil.', NULL, 'Caution is advised during concomitant use of carbamazepine with diltiazem or verapamil. Serum carbamazepine levels and pharmacologic response should be monitored more closely following the initiation or discontinuation of CCB therapy, and the carbamazepine dosage adjusted as necessary. Some clinicians recommend empirically reducing the carbamazepine dosage by 50% upon initiation of the CCB. Patients should be advised to contact their physician if they experience potential signs and symptoms of carbamazepine toxicity such as headache, nausea, vomiting, dizziness, confusion, slurred speech, nystagmus, visual disturbances, tremors, and ataxia. If carbamazepine is added to existing diltiazem or verapamil therapy, patients should be monitored for potentially reduced blood pressure and cardiac effects.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56295/', '[1] Spina E, Pisani F, Perucca E 