INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56247, 'Elagolix', 'Carbamazepine', 'Moderate', 'Coadministration with inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may decrease the plasma concentrations of elagolix, which is a substrate of both the metabolic isoenzyme and efflux transporter.', NULL, 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers.', NULL, 'The potential for diminished pharmacologic effects of elagolix should be considered during coadministration with CYP450 3A4 and/or P-gp inducers. Alternative treatments or a dose adjustment of elagolix may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56319/', '[1] "Product Information. Orilissa (elagolix)." AbbVie US LLC, North Chicago, IL.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56248, 'Eliglustat', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of eliglustat, which is primarily metabolized by CYP450 2D6 and, to a lesser extent, CYP450 3A4. Eliglustat is also a substrate of P-gp efflux transporter.', NULL, 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', NULL, 'Concomitant use of eliglustat with potent CYP450 3A4 or P-gp inducers should generally be avoided due to the potential for significantly reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56320/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[3] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[4] "Product Information. Cerdelga (eliglustat)." Genzyme Corporation, Cambridge, MA.[5] "Product Information. Trikafta (elexacaftor/ivacaftor/tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[6] "Product Information. Kalydeco (ivacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[7] "Product Information. Orkambi (ivacaftor-lumacaftor)." Vertex Pharmaceuticals, Cambridge, MA.[8] "Product Information. Symdeko 4-Week (ivacaftor-tezacaftor)." Vertex Pharmaceuticals, Cambridge, MA.', 1767365204);
