INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56281, 'Etoposide', 'Carbamazepine', 'Moderate', 'Some anticonvulsants such as carbamazepine, phenobarbital, phenytoin, and primidone may reduce the systemic exposure of etoposide. The proposed mechanism is accelerated clearance of etoposide due to induction of hepatic CYP450 enzymes by these anticonvulsants.', NULL, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to etoposide, and the dosage adjusted accordingly as necessary.', NULL, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to etoposide, and the dosage adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56353/', '[1] Rodman JH, Murry DJ, Madden T, Santana VM "Pharmacokinetics of high doses of etoposide and the influence of anticonvulsants in pediatric cancer patients." Clin Pharmacol Ther 51 (1992): 156[2] Rodman JH, Murry DJ, Madden T, Santana VM "Altered etoposide pharmacokinetics and time to engraftment in pediatric patients undergoing autologous bone marrow transplantation." J Clin Oncol 12 (1994): 2390-7', 1767365204);
