INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56283, 'Everolimus', 'Carbamazepine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein (P-gp) may significantly decrease the blood concentrations and pharmacologic effects of everolimus.', NULL, 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided.', NULL, 'Concomitant use of everolimus with potent CYP450 3A4 and/or P-gp inducers should generally be avoided. Alternative therapeutic agents with less enzyme induction potential should be considered. Some manufacturers recommend that, if concomitant use is unavoidable, the daily everolimus dose should be doubled to achieve the recommended therapeutic range for the condition being treated. Please refer to the manufacturer''s labeling for specific dosing information. If the potent CYP450 3A4 and/or P-gp inducer is discontinued, the everolimus dosage should be returned to the dosage used before the potent inducer was commenced after a washout period of approximately 3 to 5 days. Everolimus whole blood trough levels should be closely monitored during treatment, particularly 2 weeks after a dose increase, and 2 weeks after discontinuation of the potent inducers.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56355/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Canadian Pharmacists Association "e-CPS. Available from: URL: http://www.pharmacists.ca/function/Subscriptions/ecps.cfm?link=eCPS_quikLink."[4] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[5] "Product Information. Zortress (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.[6] "Product Information. Afinitor (everolimus)." Novartis Pharmaceuticals, East Hanover, NJ.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56284, 'Exemestane', 'Carbamazepine', 'Moderate', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of exemestane, which is primarily metabolized by the isoenzyme.', NULL, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St.', NULL, 'The manufacturer recommends increasing the dosage of exemestane to 50 mg once daily when used with potent CYP450 3A4 inducers such as carbamazepine, enzalutamide, lumacaftor, mitotane, phenobarbital, phenytoin, primidone (partially metabolized to phenobarbital), rifamycins, and St. John''s wort. However, it has also been suggested that suppression of estrogen levels is not affected by the interaction, thus dosage adjustment of exemestane is not required. The extent to which other, less potent CYP450 3A4 inducers may interact with exemestane is unknown. Caution is advised if they are used with exemestane.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56356/', '[1] "Product Information. Aromasin (exemestane)" Pharmacia & Upjohn, Kalamazoo, MI.[2] "Product Information. Diabinese (chlorpropamide)." Pfizer US Pharmaceuticals, New York, NY.[3] Skillman TG, Feldman JM "The pharmacology of sulfonylureas." Am J Med 70 (1981): 361-72[4] Jerntorp P, Almer LO "Chlorpropamide-alcohol flushing in relation to macroangiopathy and peripheral neuropathy in non-insulin dependent diabetes." 