INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56349, 'Carbamazepine', 'Lesinurad', 'Moderate', 'Coadministration with inducers of CYP450 2C9 may decrease the plasma concentrations of lesinurad. According to the manufacturer, administration of lesinurad with the potent CYP450 inducer rifampin (600 mg daily) decreased lesinurad peak plasma concentration (Cmax) and systemic exposure (AUC) by approximately 25% and 40%, respectively, compared to administration of lesinurad alone.', NULL, 'The potential for diminished pharmacologic effects of lesinurad should be considered during coadministration with CYP450 2C9 inducers.', NULL, 'The potential for diminished pharmacologic effects of lesinurad should be considered during coadministration with CYP450 2C9 inducers. Alternative treatments may be required if an interaction is suspected.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56421/', '[1] "Product Information. Zurampic (lesinurad)." Astra-Zeneca Pharmaceuticals, Wilmington, DE.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365204);
