INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56408, 'Carbamazepine', 'Midazolam', 'Moderate', 'Some antiepileptic agents such as carbamazepine and phenytoin significantly reduce the effect of oral midazolam. The mechanism may be due to enhanced gut and liver metabolism of midazolam (via CYP450 3A4 enzyme induction by carbamazepine). Parenteral midazolam is not likely to be affected. Other oral benzodiazepines metabolized by the 3A4 isoenzyme may interact similarly.', NULL, 'Patients receiving this combination should be monitored for clinical response.', NULL, 'Patients receiving this combination should be monitored for clinical response. Alternative oral sedative hypnotics may be preferable in patients receiving carbamazepine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56480/', '[1] Vajda FJ, Prineas RJ, Lovell RR "Interaction between phenytoin and the benzodiazepines." Br Med J 1 (1971): 346[2] Shuttleworth E, Wise G, Paulson G "Choreoathetosis and diphenylhydantoin intoxication." JAMA 230 (1974): 1170-1[3] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[4] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[5] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[6] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 46