INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56415, 'Carbamazepine', 'Modafinil', 'Moderate', 'Coadministration of modafinil (the racemate) or armodafinil (the R-enantiomer) with carbamazepine may result in a reduction in the plasma concentrations and therapeutic effects of both drugs. The proposed mechanism involves increased clearance of each drug due to mutual induction of CYP450 3A4/5-mediated metabolism of each drug. Modafinil and armodafinil are both partially metabolized by the isoenzyme.', NULL, 'Caution is advised if modafinil or armodafinil are used concomitantly with carbamazepine.', NULL, 'Caution is advised if modafinil or armodafinil are used concomitantly with carbamazepine. Patients should be monitored for a reduction in the pharmacologic response to each drug. Dosage adjustments as well as clinical and laboratory monitoring of each drug should be considered whenever either drug is added to or withdrawn from therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56487/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Provigil (modafinil)." Cephalon, Inc, West Chester, PA.[4] "Product Information. Nuvigil (armodafinil)." Cephalon Inc, West Chester, PA.[5] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[6] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[7] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine i