INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56421, 'Carbamazepine', 'Naldemedine', 'Major', 'Coadministration with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of naldemedine, which is primarily metabolized by CYP450 3A4 to nor-naldemedine and to a minor extent by UGT1A3 to naldemedine 3-G. Both metabolites have demonstrated antagonistic activity for opioid receptors, but with less potency than the parent drug.', NULL, 'Concomitant use of naldemedine with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', NULL, 'Concomitant use of naldemedine with potent CYP450 3A4 inducers should generally be avoided due to the potential for reduced efficacy.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56493/', '[1] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[3] "Product Information. Symproic (naldemedine)." Shionogi USA Inc, Florham Park, NJ.', 1767365204);
