INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56460, 'Carbamazepine', 'Paliperidone', 'Moderate', 'The concomitant use of potent inducers of P-glycoprotein (P-gp) efflux transporters such as carbamazepine may reduce plasma concentrations of paliperidone and its efficacy. The proposed mechanism is induction of renal P-gp. P-gp induction may take 2 to 3 weeks to reach steady state after a P-gp-inducing drug is initiated, and as long to wear off after the inducer is discontinued. Other P-gp inducers such as rifampin and St. John''s wort may also interact, although clinical data have not been reported.', NULL, 'Patients should be monitored for signs of reduced efficacy during concomitant use and for 2 to 3 weeks after a P-gp-inducing drug is added, discontinued, or its dose is changed.', NULL, 'Patients should be monitored for signs of reduced efficacy during concomitant use and for 2 to 3 weeks after a P-gp-inducing drug is added, discontinued, or its dose is changed. Dose adjustments of paliperidone may be required.', 'Absorption', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56532/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8[4] "Product Information. Invega (paliperidone)." Janssen Pharmaceuticals, Titusville, NJ.', 1767365204);
