INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56489, 'Carbamazepine', 'Pirfenidone', 'Moderate', 'Coadministration with potent inducers of CYP450 1A2 or other isoenzymes involved in the metabolism of pirfenidone such as CYP450 2C9 and 2C19 may significantly decrease the plasma concentrations of pirfenidone. Loss of therapeutic efficacy may result, as pirfenidone is primarily (70% to 80%) metabolized by CYP450 1A2, with minor contribution from CYP450 2C9, 2C19, 2D6, and 2E1.', NULL, 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', NULL, 'Potent inducers of CYP450 1A2, 2C9, and/or 2C19 such as phenytoin, carbamazepine, and rifampin should be discontinued prior to the initiation of pirfenidone and avoided during therapy.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56561/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[3] "Product Information. Esbriet (pirfenidone)." Intermune Inc, Brisbane, CA.[4] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56490, 'Carbamazepine', 'Pitolisant', 'Major', 'Coadministration with potent CYP450 3A4 inducers may decrease the systemic exposure and efficacy of pitolisant. The proposed mechanism is induction of CYP450 3A4-mediated metabolism of pitolisant, which has been shown to be metabolized by this isoenzyme.', NULL, 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary.', NULL, 'Patients should be assessed for loss of efficacy of pitolisant following initiation of a potent CYP450 3A4 inducer, and the pitolisant dosage adjusted as necessary. For patients already receiving a stable dose of pitolisant 8.9 mg or 17.8 mg once daily, the dose of pitolisant should be increased to double the original daily dose (i.e., 17.8 mg or 35.6 mg, respectively) over 7 days. The dosage of pitolisant should be reduced by half if concomitant use of a strong CYP450 3A4 inducer is discontinued.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56562/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Wakix (pitolisant)." Harmony Biosciences, LLC, Plymouth Meeting, PA.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767365204);
