INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56500, 'Carbamazepine', 'Prednisolone', 'Moderate', 'Carbamazepine may decrease the plasma concentrations and systemic effects of both endogenous and exogenous corticosteroids. The mechanism is accelerated corticosteroid metabolism due to induction of the CYP450 3A4 enzymatic pathway by carbamazepine.', NULL, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents.', NULL, 'Patients treated concomitantly with carbamazepine may require higher dosages of corticosteroids or adrenocorticotropic agents. Pharmacologic response to these agents should be monitored more closely whenever carbamazepine is added to or withdrawn from therapy in patients stabilized on their existing corticosteroid or adrenocorticotropic regimen, and the dosage(s) adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56572/', '[1] Privitera MR, Greden JF, Gardner RW, Ritchie JC, Calrroll BJ "Interference by carbamazepine with the dexamethasone suppresion test." Biol Psychiatry 17 (1982): 611-20[2] Levy RH "Cytochrome P450 isozymes and antiepileptic drug interactions." Epilepsia 36 Suppl 5 (1995): s8-13[3] Keitner GI, Fruzzetti AE, Miller IW, Norman WH, Brown WA "The effect of anticonvulsants on the dexamethasone suppression test." Can J Psychiatry 34 (1989): 441-3[4] Spina E, Pisani F, Perucca E "Clinically significant pharmacokinetic drug interactions with carbamazepine - an update." Clin Pharmacokinet 31 (1996): 198-214[5] Olivesi A "Modified elimination of prednisolone in epileptic patients on carbamazepine monotherapy, and in