INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56541, 'Carbamazepine', 'Risperidone', 'Moderate', 'Coadministration with carbamazepine may decrease the plasma concentrations of risperidone and its pharmacologically active metabolite, 9-hydroxyrisperidone. The proposed mechanism is carbamazepine induction of CYP450 3A4-mediated metabolism.', NULL, 'Patients treated concomitantly with carbamazepine may require a higher dosage of risperidone to control psychotic symptoms.', NULL, 'Patients treated concomitantly with carbamazepine may require a higher dosage of risperidone to control psychotic symptoms. Pharmacologic response and plasma risperidone levels should be monitored more closely whenever carbamazepine is added to or withdrawn from therapy in patients stabilized on their existing antipsychotic regimen, and the risperidone dosage adjusted as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56613/', '[1] Spina E, Avenoso A, Facciola G, Salemi M, Scordo MG, Giacobello T, Madia AG, Perucca E "Plasma concentrations of risperidone and 9-hydroxyrisperidone: Effect of comedication with carbamazepine or valproate." Ther Drug Monit 22 (2000): 481-5[2] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.[3] Spina E, Scordo MG, Avenoso A, Perucca E "Adverse drug interaction between risperidone and carbamazepine in a patient with chronic schizophrenia and deficient CYP2D6 activity." J Clin Psychopharmacol 21 (2001): 108-9[4] Garg SK, Kumar N, Bhargava VK, Prabhakar SK "Effect of grapefruit juice on carbamazepine bioavailability in patients with epilepsy." Clin Pharmacol Ther 64 (1998): 286-8[5] "Product Information. Tegretol (carbamazepine)." Novartis Pharmaceuticals, East Hanover, NJ.[6] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[7] "Product Information. Risperdal (risperidone)." Janssen Pharmaceutica, Titusville, NJ.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56542, 'Carbamazepine', 'Ritonavir', 'Moderate', 'Coadministration with ritonavir may significantly increase the plasma concentrations and pharmacologic effects of carbamazepine. The proposed mechanism is ritonavir inhibition of CYP450 3A4, the isoenzyme responsible for the metabolic clearance of carbamazepine. Theoretically, ritonavir plasma levels may decrease due to carbamazepine induction of CYP450 3A4, which also metabolizes ritonavir.', NULL, 'Given its narrow therapeutic index, caution is advised if carbamazepine must be used concomitantly with ritonavir.', NULL, 'Given its narrow therapeutic index, caution is advised if carbamazepine must be used concomitantly with ritonavir. Carbamazepine levels and pharmacologic effects should be closely monitored and the dosage adjusted accordingly, particularly following initiation or discontinuation of ritonavir in patients who are stabilized on their anticonvulsant regimen. Patients should be advised to contact their physician if they develop signs of carbamazepine toxicity such as ataxia, dizziness, nausea, vomiting, and visual disturbances. In addition, it may be appropriate to monitor patients for potentially reduced antiretroviral response due to decreased plasma levels of ritonavir and other antiretroviral agents induced by carbamazepine.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56614/', '[1] Barry M, Gibbons S, Back D, Mulcahy F "Protease inhibitors in patients with HIV disease. Clinically important pharmacokinetic considerations." Clin Pharmacokinet 32 (1997): 194-209[2] Bates DE, Herman RJ "Carbamazepine toxicity induced b