INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56551, 'Carbamazepine', 'Rucaparib', 'Moderate', 'Coadministration with strong CYP450 3A4 inducers may decrease the plasma concentrations and therapeutic effects of rucaparib, which has been shown in vitro to be a substrate of the isoenzyme.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inducers.', NULL, 'Caution is advised if rucaparib is used concomitantly with potent CYP450 3A4 inducers. Dosage adjustments as well as clinical and laboratory monitoring should be considered whenever a potent CYP450 3A4 inducer is added to or withdrawn from therapy with rucaparib.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56623/', '[1] EMA. European Medicines Agency. European Union "EMA - List of medicines under additional monitoring. Available from: URL: http://www.ema.europa.eu/ema/index.jsp?curl=pages/regulation/document_listing/document_listing_000366.jsp&mid=WC0b01ac058067c852" ([2013 - ]):[2] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363021);
