INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56571, 'Carbamazepine', 'Siponimod', 'Major', 'Coadministration with drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 may decrease the plasma concentrations of siponimod, which is primarily metabolized by these isoenzymes. This interaction includes concomitant use of siponimod with a moderate CYP450 2C9/strong CYP450 3A4 dual inducer or a moderate CYP450 2C9 inducer in combination with a separate strong CYP450 3A4 inducer.', NULL, 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.', NULL, 'Concomitant use of siponimod and drugs that cause moderate induction of CYP450 2C9 and strong induction of CYP450 3A4 (e.g., carbamazepine, enzalutamide, rifampin) is not recommended for all patients. Concomitant use of siponimod and moderate or strong CYP450 3A4 inducers (e.g., apalutamide, bosentan, dabrafenib, dexamethasone, efavirenz, eslicarbazepine, etravirine, fosphenytoin, lorlatinib, lumacaftor, mitotane, modafinil, nafcillin, nevirapine, phenobarbital, phenytoin, primidone, rifabutin, rifapentine, St. John''s wort) is not recommended for patients with CYP450 2C9*1/*3 and *2/*3 genotypes.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56643/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Mayzent (siponimod)." Novartis Pharmaceuticals, East Hanover, NJ.[3] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56572, 'Carbamazepine', 'Sirolimus', 'Major', 'Coadministration with potent inducers of CYP450 3A4 and/or P-glycoprotein may significantly decrease the plasma concentrations and pharmacologic effects of sirolimus. The mechanism probably involves reduced absorption as well as accelerated clearance of sirolimus due to induction of both intestinal P-glycoprotein drug efflux transporter and hepatic/intestinal CYP450 3A4 isoenzymes.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended.', NULL, 'Given the risk of organ rejection associated with inadequate immunosuppressant levels, the use of sirolimus with potent inducers of CYP450 3A4 and/or P-gp is not recommended. Alternative therapeutic agents with less enzyme induction potential should be considered.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56644/', '[1] Cerner Multum, Inc. "Australian Product Information." O 0[2] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Rapamune (sirolimus)" Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56573, 'Carbamazepine', 'Sodium oxybate', 'Major', 'The central nervous system and respiratory depressant effects of sodium oxybate, which is the sodium salt of gamma hydroxybutyrate (GHB), may be potentiated by concomitant use of other agents with CNS depressant effects. An increased risk of serious adverse reactions such as respiratory depression, hypotension, profound sedation, syncope, coma, and even death should be considered.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible.', NULL, 'Concomitant use of sodium oxybate with other CNS depressants should be avoided whenever possible. Otherwise, close monitoring and/or dosage reductions should be considered. If short-term use of a CNS depressant is required (e.g., post- or perioperative opioid), a temporary interruption of sodium oxybate therapy may be appropriate.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56645/', '[1] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[2] "Product Information. Xywav (calcium/magnesium/potass/sodium oxybates)." Jazz Pharmaceuticals, Palo Alto, CA.[3] "Product Information. Xyrem (sodium oxybate)." Orphan Medical, Minnetonka, MN.[4] "Product Information. Fycompa (perampanel)." Eisai Inc, Teaneck, NJ.[5] Gilman AG, Rall TW, Nies AS, Taylor P, eds. "Goodman and Gilman''s the Pharmacological Basis of Therapeutics. 8th ed." New York, NY: Pergamon Press Inc. (1990):[6] Warrington SJ, Ankier SI, Turner P "Evaluation of possible interactions between ethanol and trazodone or amitriptyline." Neuropsychobiology 15 (1986): 31-7[7] "Product Information. Rexulti (brexpiprazole)." Otsuka American Pharmaceuticals Inc, Rockville, MD.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56574, 'Carbamazepine', 'Phenylbutyric acid', 'Moderate', 'Drugs that can increase plasma ammonia levels such as corticosteroids, haloperidol, or the anticonvulsants carbamazepine, topiramate, and phenobarbital may interfere with the therapeutic effects of phenylbutyrate therapy in the management of urea cycle disorders. Corticosteroids can cause the breakdown of body protein, which may lead to increased ammonia levels. The use of haloperidol has been associated with hyperammonemia in a young child with citrullinemia, an inherited disorder of ammonia excretion.', NULL, 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', NULL, 'Plasma ammonia levels should be closely monitored when corticosteroids, haloperidol, carbamazepine, topiramate, or phenobarbital are used in patients with urea cycle disorders receiving phenylbutyrate therapy.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56646/', '[1] "Product Information. Ravicti (glycerol phenylbutyrate)." Hyperion Therapeutics Inc, South San Francisco, CA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] "Product Information. Buphenyl (sodium phenylbutyrate)." Horizon Pharma USA Inc, Northbrook, IL.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56575, 'Carbamazepine', 'Sofosbuvir', 'Major', 'Coadministration with potent inducers of P-glycoprotein (P-gp) may significantly decrease the plasma concentrations of sofosbuvir and other direct-acting antiviral agents that may be given with sofosbuvir in fixed-dose combination products such as ledipasvir and velpatasvir. Induction of P-gp-mediated efflux in the intestine decreases the oral bioavailability of these antiviral agents, which are substrates of the transporter. The interaction has been studied with rifampin, a potent inducer of P-gp and CYP450 isoenzymes.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of potent P-gp inducers should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', NULL, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiviral drug levels, concomitant use of potent P-gp inducers should generally be avoided during treatment with sofosbuvir, given either as a single-ingredient product or as a fixed-dose combination product with ledipasvir or velpatasvir.', 'Absorption', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56647/', '[1] "Product Information. Harvoni (ledipasvir-sofosbuvir)." Gilead Sciences, Foster City, CA.[2] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[3] "Product Information. Epclusa (sofosbuvir-velpatasvir)." Gilead Sciences, Foster City, CA.[4] "Product Information. Sovaldi (sofosbuvir)." Gilead Sciences, Foster City, CA.[5] Patel S, Robinson R, Burk M "Hypertensive crisis associated with St. John''s Wort." Am J Med 112 (2002): 507-8', 1767363021);
