INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56592, 'Carbamazepine', 'Tadalafil', 'Moderate', 'Coadministration with inducers of CYP450 3A4 may decrease the plasma concentrations of tadalafil, which is primarily metabolized by the isoenzyme.', NULL, 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers.', NULL, 'For the treatment of benign prostatic hyperplasia and erectile dysfunction, no dosage adjustment of tadalafil is warranted during coadministration with CYP450 3A4 inducers. However, the possibility of diminished pharmacologic effects of tadalafil should be considered. For the treatment of pulmonary arterial hypertension, use of tadalafil should be avoided in patients chronically taking potent inducers of CYP450 3A4.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56664/', '[1] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[2] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[3] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.[4] "Product Information. Cialis (tadalafil)." Lilly, Eli and Company, Indianapolis, IN.[5] "Product Information. Adcirca (tadalafil)." United Therapeutics Corporation, Silver Spring, MD.', 1767363021);
