INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56601, 'Carbamazepine', 'Temsirolimus', 'Major', 'Coadministration of temsirolimus with potent inducers of CYP450 3A4 may significantly decrease the plasma concentrations of sirolimus, a major active metabolite of temsirolimus and known substrate of CYP450 3A4.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided.', NULL, 'Concomitant use of temsirolimus with potent CYP450 3A4 inducers should generally be avoided. If coadministration is required in patients treated with temsirolimus for renal cell carcinoma, the manufacturer recommends increasing the temsirolimus dosage from 25 mg to 50 mg once a week depending on patient tolerability. Based on pharmacokinetic studies, this dosage is predicted to adjust the sirolimus systemic exposure (AUC) to the range observed without inducers. However, clinical data are lacking. The dosage should be reduced to the normally recommended dosage (i.e., 25 mg once a week) following discontinuation of the potent CYP450 3A4 inducer.', 'Metabolism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56673/', '[1] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.[2] Cerner Multum, Inc. "Australian Product Information." O 0[3] Cerner Multum, Inc. "UK Summary of Product Characteristics." O 0[4] "Product Information. Torisel (temsirolimus)." Wyeth-Ayerst Laboratories, Philadelphia, PA.', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56602, 'Carbamazepine', 'Teniposide', 'Moderate', 'Some anticonvulsants such as carbamazepine, phenobarbital, phenytoin, and primidone may reduce the systemic exposure of teniposide. The proposed mechanism is accelerated clearance of teniposide due to induction of hepatic CYP450 enzymes by these anticonvulsants.', NULL, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to teniposide, and the dosage adjusted accordingly as necessary.', NULL, 'During concomitant therapy with certain anticonvulsants, patients should be observed for potentially inadequate or altered response to teniposide, and the dosage adjusted accordingly as necessary.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56674/', '[1] Baker DK, Relling MV, Pui CH, Christensen ML, Evans WE, Rodman JH "Increased teniposide clearance with concomitant anticonvulsant therapy." J Clin Oncol 10 (1992): 311-5', 1767363021);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56603, 'Carbamazepine', 'Terfenadine', 'Moderate', 'A single case report has suggested that coadministration of carbamazepine and terfenadine may elevate free carbamazepine levels and clinical toxicity.', NULL, 'If these drugs must be used together, close monitoring for clinical and laboratory evidence of increased carbamazepine effect is indicated.', NULL, 'If these drugs must be used together, close monitoring for clinical and laboratory evidence of increased carbamazepine effect is indicated. Patients should be advised to report signs of carbamazepine toxicity (nausea, visual disturbances, dizziness, or ataxia) to their physicians. The carbamazepine dosage may require reduction.', 'Others', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56675/', '[1] Hirschfeld S, Jarosinski P "Drug interaction of terfenadine and carbamazepine." Ann Intern Med 118 (1993): 907-8[2] Honig PK, Wortham DC, Zamani K, et al "Terfenadine-ketoconazole interaction: pharmacokinetic and electrocardiographic consequences." JAMA 269 (1993): 1513-8[3] Bailey DG, Dresser GR, Kreeft JH, Munoz C, Freeman DJ, Bend JR "Grapefruit-felodipine interaction: Effect of unprocessed fruit and probable active ingredients." Clin Pharmacol Ther 68 (2000): 468-77[4] Bailey DG, Malcolm J, Arnold O, Spence JD "Grapefruit juice-drug interactions." Br J Clin Pharmacol 46 (1998): 101-10[5] Zimmermann M, Duruz H, Guinand O, et al "Torsades de Pointes after treatment with terfenadine and ketoconazole." Eur Heart J 13 (1992): 1002-3[6] Honig PK, Wortham DC, Lazarev A, Cantilena LR "Grapefruit juice alters the systemic bioavailability and cardiac repol