INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56733, 'Entacapone', 'Levodopa', 'Moderate', 'When catechol-O-methyltransferase (COMT) inhibitors are administered together with levodopa/carbidopa, they may increase the relative bioavailability (AUC) of levodopa. This is due to a decrease in levodopa clearance resulting in a prolongation of the terminal elimination half-life of levodopa (from approximately 2 hours to 3.5 hours). Adverse effects such as dyskinesia, somnolence, and orthostatic hypotension may be potentiated. In the presence of the decarboxylase inhibitor carbidopa, COMT is the major metabolizing enzyme for levodopa.', NULL, 'Although COMT inhibitors are intended for use with levodopa/carbidopa, clinicians should be aware that dose reduction of levodopa may be necessary during coadministration.', NULL, 'Although COMT inhibitors are intended for use with levodopa/carbidopa, clinicians should be aware that dose reduction of levodopa may be necessary during coadministration. This is especially true if the patient is experiencing dyskinesia induced by levodopa. Use with caution in patients with severe dyskinesia or dystonia. Likewise, when discontinuing a COMT inhibitor, monitor patients and consider adjustment of other dopaminergic therapies as needed. In addition, some authorities advise that opicapone should be administered as a once-daily dose at least one hour before or after combinations containing levodopa so as to avoid any interaction with the absorption of levodopa (AU, UK).', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56809/', '[1] "Product Information. Tasmar (tolcapone)." Valeant Pharmaceuticals, Costa Mesa