INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (56883, 'Sodium bicarbonate', 'Levodopa', 'Minor', 'Antacids and some oral aluminum, calcium, or magnesium containing preparations may increase the absorption of levodopa. The mechanisms may be related to increased gastric pH and/or decreased gastric emptying time, resulting in less gastric degradation of levodopa and more levodopa absorption from the small intestine. This effect may be beneficial in some patients, but also difficult to predict and monitor. For most patients, this interaction is not clinically significant and no specific intervention would be required. Dosing intervals could be spaced by at least 2 hours if it is preferred to avoid this interaction.', NULL, '-', NULL, '-', 'Absorption', NULL, NULL, 'Minor', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/56959/', '[1] "Product Information. Sinemet (carbidopa-levodopa)." DuPont Pharmaceuticals, Wilmington, DE.[2] Fermaglich J, O''Dougherty DS "Effect of gastric motility on levodopa." Dis Nerv Syst 33 (1972): 624-5[3] Rivera-Calimlin L, Dujovne CA, Morgan JP, Lasagna L, Bianchine JR "L-dopa treatment failure: explanation and correction." Br Med J 4 (1970): 93-4[4] Rivera-Calimlim L, Dujovne CA, Morgan JP, Lasagna L, Bianchine JR "Absorption and metabolism of L-dopa by the human stomach." Eur J Clin Invest 1 (1971): 313-20[5] Wohlt PD, Zheng L, Gunderson S, Balzar SA, Johnson BD, Fish JT "Recommendations for the use of medications with continuous enteral nutrition." Am J Health Syst Pharm 66 (2009): 1438-67', 1767363021);
