INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357719, 'Cefditoren', 'Omeprazole', 'Moderate', 'Coadministration with H2-receptor antagonists or other agents that reduce stomach acid (e.g., proton pump inhibitors) may decrease the oral absorption and plasma concentrations of cefditoren pivoxil.', 'DDInter', 1767317475, 'The manufacturer recommends that cefditoren pivoxil not be administered concomitantly with H2-receptor antagonists or other agents that reduce stomach acid.', 'Absorption', 'The manufacturer recommends that cefditoren pivoxil not be administered concomitantly with H2-receptor antagonists or other agents that reduce stomach acid.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357720, 'Cefpodoxime', 'Omeprazole', 'Moderate', 'The coadministration with H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH may reduce the oral bioavailability of cefpodoxime proxetil and cefuroxime axetil. The proposed mechanism is a pH-dependent reduction in drug dissolution and absorption.', 'DDInter', 1767317475, 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil may want to avoid using H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH. An alternative antibiotic may be considered if these medications cannot be discontinued.', 'Absorption', 'Until further data are available, patients treated with cefpodoxime proxetil or cefuroxime axetil may want to avoid using H2-receptor antagonists, proton pump inhibitors, or other agents that can increase gastric pH.', NULL, NULL, 'Moderate', 'pharmacodynamic');
