INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357796, 'Omeprazole', 'Lonafarnib', 'Moderate', 'Coadministration with lonafarnib may increase the plasma concentrations of drugs that are substrates of the CYP450 2C19 isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 2C19, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever lonafarnib is added to or withdrawn from therapy. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Caution is advised when lonafarnib is used concomitantly with drugs that are substrates of CYP450 2C19, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357797, 'Omeprazole', 'Lovastatin', 'Moderate', 'A case report suggests that coadministration with esomeprazole may increase the plasma concentrations of atorvastatin and the associated risk of myopathy. The proposed mechanism is competitive inhibition of intestinal P-glycoprotein, resulting in decreased drug secretion into the intestinal lumen and increased drug bioavailability. Another, perhaps minor mechanism is competitive inhibition of CYP450 3A4 metabolism.', 'DDInter', 1767317475, 'Because of the increased risk of musculoskeletal toxicity associated with high levels of HMG-CoA reductase inhibitory activity in plasma, patients treated with atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) should be monitored more closely during concomitant use of proton pump inhibitors. All patients treated with HMG-CoA reductase inhibitors should be advised to promptly report to their physician any unexplained muscle pain, tenderness, or weakness, particularly if accompanied by malaise or fever. Therapy should be discontinued if creatine kinase is markedly elevated or if myopathy is suspected or diagnosed.', 'Metabolism, Absorption', 'Because of the increased risk of musculoskeletal toxicity associated with high levels of HMG-CoA reductase inhibitory activity in plasma, patients treated with atorvastatin, lovastatin, simvastatin, and red yeast rice (which contains lovastatin) should be monitored more closely during concomitant use of proton pump inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357798, 'Acetylsalicylic acid', 'Mefenamic acid', 'Moderate', 'The combined use of low-dose or high-dose aspirin with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation. Aspirin at anti-inflammatory dosages or higher may also decrease the plasma concentrations of many NSAIDs. The decreases have ranged from none or small (piroxicam, meloxicam, naproxen, tolmetin) to substantial (flurbiprofen, ibuprofen). However, the therapeutic response does not appear to be affected.', 'DDInter', 1767317475, 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs. Concomitant administration of NSAIDs is considered contraindicated or not recommended with aspirin at analgesic/anti-inflammatory dosages by many NSAID manufacturers. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as abdominal pain, bloating, sudden dizziness or lightheadedness, nausea, vomiting, hematemesis, anorexia, and melena.', 'Synergism', 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357799, 'Acetylsalicylic acid', 'Meloxicam', 'Moderate', 'The combined use of low-dose or high-dose aspirin with other nonsteroidal anti-inflammatory drugs (NSAIDs) may increase the potential for serious gastrointestinal (GI) toxicity, including inflammation, bleeding, ulceration, and perforation. Aspirin at anti-inflammatory dosages or higher may also decrease the plasma concentrations of many NSAIDs. The decreases have ranged from none or small (piroxicam, meloxicam, naproxen, tolmetin) to substantial (flurbiprofen, ibuprofen). However, the therapeutic response does not appear to be affected.', 'DDInter', 1767317475, 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs. Concomitant administration of NSAIDs is considered contraindicated or not recommended with aspirin at analgesic/anti-inflammatory dosages by many NSAID manufacturers. During concomitant therapy, patients should be advised to take the medications with food and to immediately report signs and symptoms of GI ulceration and bleeding such as abdominal pain, bloating, sudden dizziness or lightheadedness, nausea, vomiting, hematemesis, anorexia, and melena.', 'Synergism', 'Caution is advised if aspirin, particularly at anti-inflammatory dosages, is used with other NSAIDs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1357800, 'Omeprazole', 'Mephenytoin', 'Moderate', 'Omeprazole may increase phenytoin serum concentrations and the risk of toxicity. The proposed mechanism is decreased clearance due to omeprazole-mediated inhibition of CYP450 2C19. Omeprazole may interact in a similar manner with other hydantoins. The clinical significance is unknown; however, higher omeprazole doses (more than 40 mg/day) may be more likely to interact.', 'DDInter', 1767317475, 'Clinical and laboratory monitoring may be appropriate whenever omeprazole is added to or withdrawn from therapy with phenytoin. Dosage adjustments may be considered if an interaction is suspected. Patients should be advised to notify their doctor if they experience symptoms of phenytoin toxicity (e.g., drowsiness, visual disturbances, change in mental status, seizures, nausea, or ataxia).', 'Metabolism', 'Clinical and laboratory monitoring may be appropriate whenever omeprazole is added to or withdrawn from therapy with phenytoin.', NULL, NULL, 'Moderate', 'pharmacodynamic');
