INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358194, 'Insulin human (zinc extended)', 'Atazanavir', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358195, 'Irinotecan', 'Atazanavir', 'Major', 'Coadministration with atazanavir-ritonavir is predicted to significantly increase the plasma concentrations of irinotecan and its metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 1767317475, 'Concomitant use of irinotecan with atazanavir-ritonavir or atazanavir-cobicistat is considered contraindicated by some authorities. Atazanavir-ritonavir should be discontinued for at least one week before initiation of treatment with irinotecan.', 'Metabolism', 'Concomitant use of irinotecan with atazanavir-ritonavir or atazanavir-cobicistat is considered contraindicated by some authorities.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358196, 'Irinotecan (liposomal)', 'Atazanavir', 'Major', 'Coadministration with atazanavir-ritonavir is predicted to significantly increase the plasma concentrations of irinotecan and its metabolite, SN-38. CYP450 3A4 and UGT1A1 are the isoenzymes responsible for the metabolic conversion of irinotecan to its inactive metabolite, APC. Inhibition of APC formation results in more irinotecan metabolism to SN-38, an active and toxic metabolite. High plasma levels of irinotecan and SN-38 may increase the risk of potentially fatal toxicities such as severe diarrhea, neutropenia, sepsis, and thromboembolism.', 'DDInter', 1767317475, 'Concomitant use of irinotecan with atazanavir-ritonavir or atazanavir-cobicistat is considered contraindicated by some authorities. Atazanavir-ritonavir should be discontinued for at least one week before initiation of treatment with irinotecan.', 'Metabolism', 'Concomitant use of irinotecan with atazanavir-ritonavir or atazanavir-cobicistat is considered contraindicated by some authorities.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358197, 'Isavuconazonium', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of isavuconazole, which is primarily metabolized by CYP450 3A4 and 3A5 and subsequently by uridine diphosphate glucuronosyltransferases (UGT).', 'DDInter', 1767317475, 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated. Ritonavir given at low dosages as a pharmacokinetic booster may be used with caution, but is contraindicated at high dosages (e.g., 400 mg every 12 hours).', 'Metabolism', 'Concomitant use of isavuconazonium sulfate with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358198, 'Isoniazid', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone. Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358199, 'Isradipine', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves atazanavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. ECG monitoring may be appropriate due to potential additive effects on the PR interval. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358200, 'Istradefylline', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and risk of adverse effects of istradefylline, which is primarily metabolized by CYP450 3A4 and 1A1.', 'DDInter', 1767317475, 'The manufacturer suggests that the maximum dose of istradefylline when coadministered with potent CYP450 3A4 inhibitors be reduced to 20 mg once daily. Patients should be monitored for adverse effects including dyskinesia, impulse control issues, dizziness, constipation, nausea, hallucinations, and insomnia.', 'Metabolism', 'The manufacturer suggests that the maximum dose of istradefylline when coadministered with potent CYP450 3A4 inhibitors be reduced to 20 mg once daily.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358201, 'Itraconazole', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone. Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358202, 'Ivabradine', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivabradine, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ivabradine with potent CYP450 3A4 inhibitors is considered contraindicated. Some authorities consider concomitant administration of ivabradine and itraconazole to be contraindicated during and for 2 weeks after treatment with itraconazole.', 'Metabolism', 'Concomitant use of ivabradine with potent CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358203, 'Ivacaftor', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivacaftor, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'For ivacaftor -The ivacaftor dose should be reduced from 1 tablet or 1 packet of oral granules twice a day to 1 tablet or 1 packet of oral granules twice a week (i.e., every 3 to 4 days) during coadministration with potent CYP450 3A4 inhibitors. For example, a patient taking ivacaftor 150 mg tablet twice a day should reduce their ivacaftor dose to a 150 mg tablet twice a week and a patient taking ivacaftor 50 mg oral granule packet twice a day should reduce their ivacaftor dose to 50 mg oral granule packet twice a week, etc.', 'Metabolism', 'For ivacaftor -The ivacaftor dose should be reduced from 1 tablet or 1 packet of oral granules twice a day to 1 tablet or 1 packet of oral granules twice a week (i.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358204, 'Ivermectin', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of ivermectin and the risk of adverse effects. The proposed mechanism, based on in vitro data, is decreased clearance due to inhibition of CYP450 3A4, the primary isoenzyme responsible for the metabolic clearance of ivermectin.', 'DDInter', 1767317475, 'Caution is advised when ivermectin must be used concomitantly with potent CYP450 3A4 inhibitors. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate whenever a potent CYP450 3A4 inhibitor is added to or withdrawn from therapy.', 'Metabolism', 'Caution is advised when ivermectin must be used concomitantly with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358205, 'Atazanavir', 'Ivosidenib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ivosidenib, which is primarily metabolized by the isoenzyme. Coadministration with ivosidenib may decrease the plasma concentrations of drugs that are primarily metabolized by CYP450 3A4, including many of the known potent inhibitors of the isoenzyme such as clarithromycin, conivaptan, delavirdine, idelalisib, itraconazole, ketoconazole, nefazodone, posaconazole, telithromycin, and all protease inhibitors. Ivosidenib is an inducer of CYP450 3A4 and induces its own metabolism following multiple dosing.', 'DDInter', 1767317475, 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided. If coadministration is required, a reduction of the ivosidenib dosage to 250 mg once daily is recommended. Following discontinuation of the potent inhibitor for at least 5 half-lives, ivosidenib therapy should be returned to the recommended dosage of 500 mg once daily. Monitoring of electrocardiograms and electrolytes should be conducted regularly.', 'Metabolism', 'Concomitant use of ivosidenib with potent CYP450 3A4 inhibitors, particularly those that are also substrates of CYP450 3A4, should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358206, 'Atazanavir', 'Ixabepilone', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of ixabepilone, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of ixabepilone with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is necessary, a reduction of the ixabepilone dosage to 20 mg/m2 should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the ixabepilone systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the ixabepilone dosage is adjusted upward to the indicated dosage.', 'Metabolism', 'Concomitant use of ixabepilone with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358207, 'Atazanavir', 'Ketoconazole', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone. Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358208, 'Atazanavir', 'Labetalol', 'Major', 'Atazanavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, digoxin, lacosamide, mefloquine, verapamil) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., verapamil), since atazanavir is an inhibitor of that isoenzyme.', 'Synergism', 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358209, 'Atazanavir', 'Lacosamide', 'Major', 'Atazanavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, digoxin, lacosamide, mefloquine, verapamil) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., verapamil), since atazanavir is an inhibitor of that isoenzyme.', 'Synergism', 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358210, 'Atazanavir', 'Lamotrigine', 'Moderate', 'The concomitant use of lamotrigine and atazanavir-ritonavir may reduce plasma concentrations of lamotrigine. The mechanism is induction of glucuronidation.', 'DDInter', 1767317475, 'No dose adjustments are required when lamotrigine is added to an existing regimen of atazanavir-ritonavir. However, if the regimen includes a maintenance dose of lamotrigine and no other glucuronidation inducers, a dosage increase may be required when atazanavir-ritonavir is added to the regimen, based on clinical response and plasma levels. A dosage decrease may be required upon discontinuation of atazanavir-ritonavir. Monitoring of plasma lamotrigine levels is recommended before and after atazanavir-ritonavir is initiated or discontinued. The manufacturer''s labeling should be consulted for the appropriate dosage regimen.', 'Metabolism', 'No dose adjustments are required when lamotrigine is added to an existing regimen of atazanavir-ritonavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358211, 'Atazanavir', 'Lansoprazole', 'Major', 'Concurrent use of proton pump inhibitors may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in treatment-experienced patients receiving atazanavir with or without ritonavir or cobicistat. In treatment-naive patients receiving atazanavir with ritonavir or cobicistat, it is recommended that the dose of the proton-pump inhibitor not exceed 20 mg/day of omeprazole, or equivalent, and doses should be separated by 12 hours. Atazanavir without ritonavir or cobicistat should not be coadministered with proton pump inhibitors in treatment-naive patients.', 'Absorption', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in treatment-experienced patients receiving atazanavir with or without ritonavir or cobicistat.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358212, 'Atazanavir', 'Lapatinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of lapatinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of lapatinib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of lapatinib during and for 2 weeks after treatment with itraconazole. If coadministration is required, the manufacturer recommends reducing the lapatinib dosage to 500 mg once a day. However, clinical data are lacking. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the lapatinib dosage is adjusted upward to the indicated dosage (i.e., 1250 mg once a day).', 'Metabolism', 'Concomitant use of lapatinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358213, 'Atazanavir', 'Larotrectinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of larotrectinib, which is primarily metabolized by the isoenzyme. Increased exposure to larotrectinib may increase the risk of adverse effects such as neurotoxicity (delirium, dysarthria, dizziness, gait disturbance, paresthesia, encephalopathy, memory impairment, tremor) and hepatotoxicity (elevations in liver transaminases).', 'DDInter', 1767317475, 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, the manufacturer recommends reducing the larotrectinib dose by 50%. After the inhibitor has been discontinued for 3 to 5 elimination half-lives, the larotrectinib dose that was taken prior to initiating the inhibitor may be resumed.', 'Metabolism', 'Concomitant use of larotrectinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358214, 'Atazanavir', 'Lasmiditan', 'Moderate', 'Coadministration of lasmiditan and agents that lower heart rate may increase the risk of bradycardia.', 'DDInter', 1767317475, 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk. Consider monitoring heart rate in patients for whom the estimated decrease in heart rate may not be tolerated.', 'Synergism', 'Caution is advised during coadministration of lasmiditan and agents that lower heart rate if the estimated decrease in heart rate may present a clinical risk.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358215, 'Atazanavir', 'Lefamulin', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 and/or P-gp may significantly increase the plasma concentrations of lefamulin, which is a substrate of both the isoenzyme and efflux transporter. Coadministration of intravenous lefamulin with oral ketoconazole increased mean lefamulin Cmax and AUC by 6% and 31%, respectively. Increased exposure to lefamulin may increase the risk of QT interval prolongation, which has been associated with ventricular arrhythmias including torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', 'Metabolism', 'Concomitant use of lefamulin with potent inhibitors of CYP450 3A4 and/or P-gp should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358216, 'Atazanavir', 'Lemborexant', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of lemborexant, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', 'Metabolism', 'Concomitant use of lemborexant with potent or moderate CYP450 3A4 inhibitors should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358217, 'Atazanavir', 'Lesinurad', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358218, 'Atazanavir', 'Letermovir', 'Moderate', 'Coadministration with letermovir may increase the plasma concentrations of drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or organic anion transporting polypeptide protein (OATP) 1B1 and 1B3. Letermovir has been shown to be a reversible inhibitor of CYP450 2C8 in vitro, although its effect on CYP450 2C8 substrates has not been evaluated clinically. Letermovir is also a time-dependent inhibitor and inducer of CYP450 3A4 in vitro.', 'DDInter', 1767317475, 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range. Dosage adjustments as well as clinical and laboratory monitoring may be appropriate for some drugs whenever letermovir is added to or withdrawn from therapy. Moreover, clinicians should be aware that the magnitude of CYP450 3A- and OATP1B1/3-mediated drug interactions with coadministered drugs may be different when letermovir is used with cyclosporine. The combined effect of the two drugs on CYP450 3A4 may be similar to that of a strong CYP450 3A4 inhibitor, hence clinicians should refer to the prescribing information for dosing recommendations of the CYP450 3A4 substrate with a strong CYP450 3A4 inhibitor. Similarly, letermovir and cyclosporine may demonstrate some additive effects on OATP1B1 inhibition, although cyclosporine by itself is already a strong OATP1B1/3 inhibitor.', 'Metabolism', 'Caution is advised when letermovir is used concurrently with drugs that are substrates of CYP450 2C8, CYP450 3A4, and/or OATP 1B1 and 1B3, particularly those with a narrow therapeutic range.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358219, 'Atazanavir', 'Levamlodipine', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves atazanavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. ECG monitoring may be appropriate due to potential additive effects on the PR interval. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358220, 'Atazanavir', 'Levobupivacaine', 'Moderate', 'Coadministration with inhibitors of CYP450 1A2 and/or 3A4 may increase the plasma concentrations of levobupivacaine, which is primarily metabolized by these isoenzymes.', 'DDInter', 1767317475, 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.g., quinolones, deferasirox, mexiletine, propafenone, ticlopidine, zileuton) or CYP450 3A4 (e.g., azole antifungals, macrolide antibiotics, protease inhibitors, kinase inhibitors, aprepitant, conivaptan, cyclosporine, delavirdine, diltiazem, nefazodone, verapamil). Particular caution and dosage adjustment of levobupivacaine may be warranted during concomitant use of both a CYP450 1A2 inhibitor and a CYP450 3A4 inhibitor, or a drug that inhibits both isoenzymes (e.g., fluvoxamine). Patients should be monitored for early signs and symptoms of central nervous system toxicity such as dizziness, drowsiness, lightheadedness, anxiety, restlessness, incoherent speech, tremors, twitching, numbness and tingling of the mouth and lips, metallic taste, tinnitus, and blurred vision, as well as more serious reactions including convulsions, hypotension, arrhythmias, and cardiorespiratory depression.', 'Metabolism', 'Caution may be advisable if levobupivacaine is administered to patients receiving inhibitors of CYP450 1A2 (e.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358221, 'Atazanavir', 'Levacetylmethadol', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations and duration of action of levomethadyl acetate, which is a substrate of the isoenzyme. High plasma levels of levomethadyl acetate may increase the risk of QT interval prolongation, which is thought to be associated with serious cardiac events such as ventricular arrhythmias (e.g., torsade de pointes, ventricular tachycardia), cardiac arrest, and sudden death. Cases of QT prolongation and torsade de pointes have been reported with levomethadyl acetate alone during postmarketing use.', 'DDInter', 1767317475, 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', 'Metabolism', 'Concomitant use of levomethadyl acetate with CYP450 3A4 inhibitors is considered contraindicated.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358222, 'Atazanavir', 'Levomilnacipran', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of levomilnacipran, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'The dosage of levomilnacipran should not exceed 80 mg once daily when used with potent CYP450 3A4 inhibitors.', 'Metabolism', 'The dosage of levomilnacipran should not exceed 80 mg once daily when used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358223, 'Atazanavir', 'Levonorgestrel', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of ethinyl estradiol and norethindrone and possibly other contraceptive hormones. The mechanism is atazanavir inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 1767317475, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', 'Metabolism', 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358224, 'Atazanavir', 'Linagliptin', 'Moderate', 'The efficacy of insulin and other antidiabetic agents may be diminished by certain drugs, including atypical antipsychotics, corticosteroids, diuretics, estrogens, gonadotropin-releasing hormone agonists, human growth hormone, phenothiazines, progestins, protease inhibitors, sympathomimetic amines, thyroid hormones, L-asparaginase, alpelisib, copanlisib, danazol, diazoxide, isoniazid, megestrol, omacetaxine, phenytoin, tagraxofusp, temsirolimus, as well as pharmacologic dosages of nicotinic acid and adrenocorticotropic agents. These drugs may interfere with blood glucose control because they can cause hyperglycemia, glucose intolerance, new-onset diabetes mellitus, and/or exacerbation of preexisting diabetes.', 'DDInter', 1767317475, 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary. Patients should be advised to notify their physician if their blood glucose is consistently high or if they experience symptoms of severe hyperglycemia such as excessive thirst and increases in the volume or frequency of urination.', 'Antagonism', 'Close clinical monitoring of glycemic control is recommended following initiation or discontinuation of these drugs, and the dosages of concomitant antidiabetic agents adjusted as necessary.', NULL, NULL, 'Moderate', 'pharmacodynamic');
