INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358267, 'Atazanavir', 'Nefazodone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone. Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358268, 'Atazanavir', 'Nelfinavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of atazanavir, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone. Pharmacologic response to atazanavir should be monitored more closely whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy. In addition, many CYP450 3A4 inhibitors are also substrates of the isoenzyme, thus pharmacologic response to these agents should also be monitored during coadministration with atazanavir.', 'Metabolism', 'Caution is advised if atazanavir is used with CYP450 3A4 inhibitors, particularly potent ones like other protease inhibitors, macrolide antibiotics, itraconazole, ketoconazole, and nefazodone.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358269, 'Atazanavir', 'Neratinib', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of neratinib, which is primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', 'Metabolism', 'Given the potential for increased risk of toxicity, concomitant use of neratinib with potent or moderate inhibitors of CYP450 3A4 should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358270, 'Atazanavir', 'Nevirapine', 'Major', 'Coadministration with nevirapine may decrease the plasma concentrations of atazanavir, with or without cobicistat or low-dose ritonavir as a pharmacokinetic booster, while the plasma concentrations of nevirapine may be increased. The plasma concentrations of cobicistat may also be reduced. Reduced plasma concentrations of atazanavir may lead to a loss of therapeutic effect and development of resistance to atazanavir. Increased nevirapine plasma concentrations may increase the risk of nevirapine-associated toxicity, including hepatotoxicity, fatal and non-fatal skin reactions (e.g., Stevens Johnson syndrome), and hypersensitivity reactions. The proposed mechanisms are induction of the CYP450 3A4 isoenzyme by nevirapine and the inhibition of CYP450 3A4 by atazanavir and cobicistat.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, along with the increased risk of nevirapine-associated toxicity, it is not recommended to prescribe unboosted atazanavir or atazanavir-boosted with cobicistat or low-dose ritonavir in combination with nevirapine. Some authorities consider concomitant use of nevirapine with unboosted atazanavir or atazanavir boosted with cobicistat contraindicated (US).', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, along with the increased risk of nevirapine-associated toxicity, it is not recommended to prescribe unboosted atazanavir or atazanavir-boosted with cobicistat or low-dose ritonavir in combination with nevirapine.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358271, 'Atazanavir', 'Nicardipine', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves atazanavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. ECG monitoring may be appropriate due to potential additive effects on the PR interval. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358272, 'Atazanavir', 'Nilotinib', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of nilotinib, which is primarily metabolized by the isoenzyme. Because nilotinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Concomitant use of nilotinib with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided. Some authorities recommend avoiding concomitant use of nilotinib during and for 2 weeks after treatment with itraconazole. Should treatment with a potent inhibitor be required, the manufacturer recommends that nilotinib therapy be withheld temporarily. If concomitant use is unavoidable, a reduction of the nilotinib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the nilotinib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have frequent ECGs and be monitored for arrhythmias when QT interval is prolonged. A QTc interval exceeding 480 msec will require suspension of nilotinib therapy and immediate action to correct any concomitant risk factors before resuming treatment. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', 'Concomitant use of nilotinib with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, cobicistat, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358273, 'Atazanavir', 'Nimodipine', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations and blood pressure lowering effect of nimodipine, which is primarily metabolized by CYP450 3A4 in the intestinal mucosa and liver.', 'DDInter', 1767317475, 'The use of nimodipine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated because of a risk of significant hypotension.', 'Metabolism', 'The use of nimodipine in combination with potent CYP450 3A4 inhibitors such as itraconazole, ketoconazole, posaconazole, voriconazole, conivaptan, nefazodone, delavirdine, protease inhibitors, and ketolide and certain macrolide antibiotics is considered contraindicated because of a risk of significant hypotension.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358274, 'Atazanavir', 'Nintedanib', 'Moderate', 'Coadministration with inhibitors of P-glycoprotein (P-gp) and CYP450 3A4 may increase the plasma concentrations of nintedanib, which is a substrate of the efflux transporter and a minor substrate of the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors. Pharmacologic response to nintedanib should be monitored more closely whenever a CYP450 3A4/P-gp inhibitor is added to or withdrawn from therapy, and the nintedanib dosing adjusted or interrupted as necessary in accordance with the product labeling. Patients should be closely monitored for increased adverse effects such as liver enzyme and bilirubin elevations, diarrhea, nausea, vomiting, gastrointestinal perforation, bleeding, and arterial thromboembolic events (e.g., myocardial infarction).', 'Metabolism', 'Caution is advised if nintedanib is prescribed in combination with P-gp and CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358275, 'Atazanavir', 'Nisoldipine', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations and pharmacologic effects of calcium channel blockers, especially the dihydropyridines (e.g., amlodipine, felodipine, nicardipine, nifedipine, nisoldipine). The mechanism involves atazanavir inhibition of intestinal and hepatic CYP450 3A4, the isoenzyme primarily responsible for the metabolic clearance of most calcium channel blockers.', 'DDInter', 1767317475, 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir. Dosage reduction may be required for the calcium channel blocker, particularly if it is a dihydropyridine. ECG monitoring may be appropriate due to potential additive effects on the PR interval. Patients should be advised to seek medical attention if they experience edema or swelling of the lower extremities; sudden, unexplained weight gain; difficulty breathing; chest pain or tightness; or hypotension as indicated by dizziness, fainting, or orthostasis.', 'Metabolism', 'Close monitoring of clinical response and tolerance is recommended if calcium channel blockers are used in combination with atazanavir.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358276, 'Atazanavir', 'Nitisinone', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of and exposure to, nitisinone. The proposed mechanism is inhibition of CYP450 3A4-mediated metabolism of nitisinone, which has been shown in in vitro studies to be metabolized by this isoenzyme.', 'DDInter', 1767317475, 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors. Pharmacologic response to nitisinone should be monitored whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the nitisinone dosage adjusted as necessary. Patients should be monitored for the development of adverse effects.', 'Metabolism', 'Until more information is available, caution is advised if nitisinone is used concomitantly with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358277, 'Nizatidine', 'Atazanavir', 'Major', 'Concurrent administration of H2-receptor antagonists may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug. Subtherapeutic antiretroviral drug levels may lead to reduced viral susceptibility and development of resistance.', 'DDInter', 1767317475, 'When used with atazanavir/cobicistat or atazanavir/ritonavir once daily in adults, the dosage of the H2-receptor antagonist should be no more than the equivalent of famotidine 40 mg twice daily in antiretroviral treatment-naive patients and 20 mg twice daily in treatment-experienced patients. Atazanavir/cobicistat or atazanavir/ritonavir should be administered simultaneously with, and/or at least 10 hours after, the H2-receptor antagonist. If tenofovir is also prescribed, no additional precaution is necessary in treatment-naive patients, whereas the dosage of atazanavir/ritonavir should be increased to 400 mg/100 mg once a day in treatment-experienced patients. All three antiretroviral agents should be administered as a single daily dose with food. In the event that ritonavir is not tolerated by a treatment-naive patient, atazanavir may be administered at an increased dosage of 400 mg once daily at least 2 hours before and at least 10 hours after the H2-receptor antagonist.', 'Absorption', 'When used with atazanavir/cobicistat or atazanavir/ritonavir once daily in adults, the dosage of the H2-receptor antagonist should be no more than the equivalent of famotidine 40 mg twice daily in antiretroviral treatment-naive patients and 20 mg twice daily in treatment-experienced patients.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358278, 'Norgestrel', 'Atazanavir', 'Moderate', 'Coadministration with atazanavir may increase the plasma concentrations of ethinyl estradiol and norethindrone and possibly other contraceptive hormones. The mechanism is atazanavir inhibition of CYP450 3A4 and UGT1A1, the enzymes responsible for the metabolic clearance of contraceptive hormones.', 'DDInter', 1767317475, 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', 'Metabolism', 'The manufacturer recommends alternate methods of nonhormonal contraception during atazanavir or atazanavir-ritonavir therapy.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358279, 'Olanzapine', 'Atazanavir', 'Minor', 'According to the product labeling, clinically significant interactions are not expected between atazanavir and substrates of CYP450 1A2, 2A6, 2B6, 2C19, 2C9, 2D6, or 2E1. No particular precaution or dosage adjustment should be necessary.', 'DDInter', 1767317475, '-', 'Metabolism', '-', NULL, NULL, 'Minor', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358280, 'Olaparib', 'Atazanavir', 'Major', 'Coadministration with moderate inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of olaparib, which is primarily metabolized by the isoenzyme. Increased exposure to olaparib may increase the risk of adverse effects such as hematologic toxicity, nausea, vomiting, diarrhea, anorexia, dyspepsia, and abdominal pain or discomfort.', 'DDInter', 1767317475, 'Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible. If coadministration is required, dose reductions are recommended as follows: the olaparib tablet dosage should be reduced to 150 mg twice a day; the olaparib capsule dosage should be reduced to 200 mg twice a day. Specific dose recommendations for each formulation should be followed as the tablet and capsule formulations are not interchangeable due to differences in bioavailability. Once the CYP450 3A4 inhibitor has been discontinued for 3 to 5 elimination half-lives, the usual olaparib dose should be resumed.', 'Metabolism', 'Concomitant use of olaparib with moderate CYP450 3A4 inhibitors should be avoided whenever possible.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358281, 'Oliceridine', 'Atazanavir', 'Major', 'Coadministration with moderate or potent inhibitors of CYP450 2D6 and/or 3A4 may increase the plasma concentrations and adverse opioid effects of oliceridine, which is primarily metabolized by both isoenzymes in vitro. In addition, oliceridine concentrations may decrease following discontinuation of a concomitant moderate or potent CYP450 2D6 or 3A4 inhibitor, possibly resulting in decreased opioid efficacy or withdrawal syndrome. The effect of a CYP450 2D6 inhibitor on the pharmacokinetics of oliceridine has not been studied. However, it may be similar to that reported in CYP450 2D6 poor metabolizers, whose plasma clearance of oliceridine is reduced by approximately 50% compared to those who are nonpoor CYP450 2D6 metabolizers.', 'DDInter', 1767317475, 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4. If concomitant use is considered necessary, patients may require less frequent dosing of oliceridine and should be closely monitored for respiratory depression, sedation, and QT prolongation. If a moderate or potent CYP450 2D6 or 3A4 inhibitor is discontinued, an increase of the oliceridine dosage may be considered while monitoring for signs of opioid withdrawal until stable drug effects are achieved.', 'Metabolism', 'Caution is recommended if oliceridine is used in combination with moderate or potent inhibitors of CYP450 2D6 or 3A4.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358282, 'Orlistat', 'Atazanavir', 'Moderate', 'Orlistat may theoretically reduce the gastrointestinal absorption and therapeutic efficacy of antiretroviral agents. The mechanism may involve retention of lipophilic medicines in the gastrointestinal tract or reduced gastrointestinal tract transit time.', 'DDInter', 1767317475, 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat. Patients who take antiretroviral medications should be advised to consult their physician before using the non-prescription brand of orlistat.', 'Absorption', 'The potential reduction in antiretroviral efficacy should be considered before initiating treatment with orlistat.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358283, 'Osilodrostat', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osilodrostat, which is partially metabolized by the isoenzyme. Clinically, high plasma levels of osilodrostat may increase the risk of adverse effects such as hypocortisolism (which may lead to life-threatening adrenal insufficiency), QT prolongation (which may increase the risk of ventricular arrhythmias including torsade de pointes and sudden death), and elevated androgen and 11-deoxycorticosterone levels (the latter of which may activate mineralocorticoid receptors and cause hypokalemia, edema, and hypertension).', 'DDInter', 1767317475, 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor. Further dosage adjustments should be based on clinical response and tolerance. Patients should have regular monitoring of 24-hour urine free cortisol and serum or plasma cortisol during treatment, as well as regular evaluations for signs and symptoms of hypocortisolism such as nausea, vomiting, abdominal pain, loss of appetite, fatigue, dizziness, hypotension, abnormal electrolyte levels, and hypoglycemia. Decrease dosing or temporarily discontinue osilodrostat if patients experience symptoms of hypocortisolism or if urine free cortisol levels fall below the target range or there is a rapid decrease in cortisol levels. Stop osilodrostat and administer exogenous glucocorticoid replacement therapy if patients have symptoms of adrenal insufficiency and serum or plasma cortisol levels are below target range. Osilodrostat therapy may be restarted at a lower dosage when symptoms have resolved and cortisol values are within target range. Additionally, an electrocardiogram and serum electrolyte levels should also be obtained prior to initiating osilodrostat therapy and periodically thereafter.', 'Metabolism', 'The manufacturer recommends reducing the dosage of osilodrostat by one-half during concomitant use of a potent CYP450 3A4 inhibitor.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358284, 'Osimertinib', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of osimertinib, which has been shown in vitro to be primarily metabolized by the isoenzyme. However, no pharmacokinetic data are available from drug interaction studies. Because osimertinib is associated with concentration-dependent prolongation of the QT interval, increased levels may potentiate the risk of ventricular arrhythmias such as torsade de pointes and sudden death.', 'DDInter', 1767317475, 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided. If coadministration is required, close monitoring for adverse effects such as diarrhea, QT prolongation, torsade de pointes arrhythmia, and cardiomyopathy (e.g., cardiac failure, pulmonary edema, ejection fraction decreases) is recommended, and the osimertinib dosage adjusted accordingly or treatment discontinued as necessary.', 'Metabolism', 'Concomitant use of osimertinib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358285, 'Ospemifene', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of ospemifene, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors. Patients should be advised to seek medical attention if they develop potential signs and symptoms of thromboembolism such as chest pain, shortness of breath, and pain or swelling in the arms or legs.', 'Metabolism', 'Caution is advised when ospemifene is used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358286, 'Oxcarbazepine', 'Atazanavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358287, 'Oxycodone', 'Atazanavir', 'Major', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of oxycodone, which is substantially metabolized by the isoenzyme. Increased oxycodone concentrations could conceivably increase or prolong adverse drug effects and may cause potentially fatal respiratory depression. Because oxycodone is also partially metabolized by CYP450 2D6, the magnitude of interaction may be even greater with concomitant use of a CYP450 3A4 and a CYP450 2D6 inhibitor, or concomitant use of a drug that is a dual inhibitor of both isoenzymes.', 'DDInter', 1767317475, 'Extreme caution is advised if oxycodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.g., azole antifungal agents, protease inhibitors, aprepitant, ciprofloxacin, chloramphenicol, clarithromycin, cobicistat, conivaptan, crizotinib, delavirdine, diltiazem, dronedarone, erythromycin, fusidic acid, idelalisib, imatinib, mibefradil, mifepristone, nefazodone, netupitant, quinupristin-dalfopristin, telithromycin, verapamil) or weak inhibitors that also inhibit CYP450 2D6 (e.g., abiraterone, amiodarone, cimetidine, pazopanib, ranolazine).', 'Metabolism', 'Extreme caution is advised if oxycodone is prescribed with CYP450 3A4 inhibitors, particularly potent and moderate inhibitors (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358288, 'Ozanimod', 'Atazanavir', 'Moderate', 'The risk of transient bradycardia and atrioventricular (AV) block may be increased during initiation of ozanimod treatment in patients receiving other drugs that slow heart rate or AV conduction such as beta-blockers, certain calcium channel blockers (e.g., diltiazem, verapamil), and digitalis. Ozanimod can cause a decrease in heart rate during initiation of therapy.', 'DDInter', 1767317475, 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate. In addition, advice from a cardiologist should be sought if coadministration of ozanimod is considered in patients with significant QT prolongation (QTcF greater than 450 msec in males or 470 msec in females), patients with arrhythmias requiring treatment with Class 1a or Class III antiarrhythmic agents, patients with ischemic heart disease, heart failure, history of cardiac arrest or myocardial infarction, cerebrovascular disease, or uncontrolled hypertension. Use of ozanimod in patients with a history of second-degree Mobitz type II or higher AV block, sick-sinus syndrome, or sinoatrial heart block is considered contraindicated unless the patient has a functioning pacemaker.', 'Synergism', 'Ozanimod has not been adequately studied in patients receiving concomitant therapy with drugs that decrease heart rate.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358289, 'Paclitaxel (protein-bound)', 'Atazanavir', 'Major', 'Coadministration with certain antiretroviral agents such as protease inhibitors and delavirdine may increase the plasma concentrations and pharmacologic effects of paclitaxel. The proposed mechanism is inhibition of the CYP450 3A4-mediated metabolism of paclitaxel.', 'DDInter', 1767317475, 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine. A lower initial dosage of paclitaxel may be appropriate. Patients should be closely monitored for the development of dose-related paclitaxel toxicity such as myelosuppression, stomatitis, arthralgia, myalgia, visual disturbances and peripheral neuropathy, and the paclitaxel dosage further adjusted as necessary. Use of repaglinide with the fixed combination of atazanavir-cobicistat is not recommended.', 'Metabolism', 'Caution is advised if paclitaxel is required in patients receiving protease inhibitors and/or delavirdine.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358290, 'Palbociclib', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of palbociclib, which is a substrate of the isoenzyme. The risk of adverse effects such as infections, neutropenia, leukopenia, anemia, thrombocytopenia, anorexia, nausea, vomiting, diarrhea, stomatitis, alopecia, asthenia, peripheral neuropathy, and epistaxis may be increased.', 'DDInter', 1767317475, 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided. Alternative agents with no or minimal CYP450 3A4 inhibitory potential are recommended whenever possible. If no alternatives exist, consider reducing the palbociclib dosage to 75 mg once daily. Following discontinuation of the potent CYP450 3A4 inhibitor, the palbociclib dosage should be returned (after 3 to 5 half-lives of the inhibitor) to that used prior to initiation of the inhibitor.', 'Metabolism', 'Concomitant use of palbociclib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358291, 'Panobinostat', 'Atazanavir', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of panobinostat, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when panobinostat is prescribed with CYP450 3A4 inhibitors. Patients should be monitored for adverse effects such as nausea, vomiting, diarrhea, anorexia, peripheral edema, cardiotoxicity, ECG abnormalities, electrolyte disturbances, bleeding complications, hepatotoxicity and myelosuppression, and the dosage of panobinostat adjusted as necessary in accordance with the product labeling.', 'Metabolism', 'Caution is advised when panobinostat is prescribed with CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358292, 'Pantoprazole', 'Atazanavir', 'Major', 'Concurrent use of proton pump inhibitors may decrease the oral bioavailability of atazanavir and substantially reduce its concentrations in plasma. Atazanavir solubility decreases with increasing pH, thus inhibition of gastric acid secretion may interfere with dissolution of the drug.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in treatment-experienced patients receiving atazanavir with or without ritonavir or cobicistat. In treatment-naive patients receiving atazanavir with ritonavir or cobicistat, it is recommended that the dose of the proton-pump inhibitor not exceed 20 mg/day of omeprazole, or equivalent, and doses should be separated by 12 hours. Atazanavir without ritonavir or cobicistat should not be coadministered with proton pump inhibitors in treatment-naive patients.', 'Absorption', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, use of proton pump inhibitors is not recommended in treatment-experienced patients receiving atazanavir with or without ritonavir or cobicistat.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358293, 'Paricalcitol', 'Atazanavir', 'Moderate', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of paricalcitol, which is partially metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors. Serum calcium and phosphorus levels should be monitored closely. If clinically significant hypercalcemia develops, the paricalcitol dose should be reduced or interrupted and support therapy administered.', 'Metabolism', 'Caution is advised when paricalcitol is used with potent CYP450 3A4 inhibitors.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358294, 'Pasireotide', 'Atazanavir', 'Moderate', 'The risk of significant bradycardia and atrioventricular (AV) block may be increased during concomitant treatment of pasireotide with other drugs that slow heart rate or AV conduction such as beta-blockers, calcium channel blockers, atazanavir, digitalis, flecainide, lacosamide, mefloquine, moricizine, and propafenone.', 'DDInter', 1767317475, 'Pasireotide has not been studied in patients receiving other bradycardic drugs. Because bradycardia and AV block are recognized risk factors for torsade de pointes arrhythmia, electrocardiographic monitoring is recommended prior to initiation of pasireotide and during treatment. Dose adjustments of beta blockers and calcium channel blockers or correction of electrolyte disturbances may be necessary.', 'Synergism', 'Pasireotide has not been studied in patients receiving other bradycardic drugs.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358295, 'Pazopanib', 'Atazanavir', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may significantly increase the plasma concentrations of pazopanib, which is primarily metabolized by the isoenzyme. Although not studied, the interaction may increase the risk of QT interval prolongation and torsade de pointes arrhythmia as well as severe and fatal hepatotoxicity associated with the use of pazopanib.', 'DDInter', 1767317475, 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided. Some authorities recommend avoiding concomitant use of pazopanib during and for 2 weeks after treatment with itraconazole. If coadministration is unavoidable, a reduction of the pazopanib dosage to 400 mg once daily should be considered. Based on pharmacokinetic studies, this dosage is predicted to adjust the pazopanib systemic exposure (AUC) to the range observed without inhibitors. However, clinical data are lacking. Patients should have liver function tests (ALT, AST, bilirubin), electrocardiograms, and serum electrolyte levels performed at baseline and regular intervals as recommended in the product labeling. Further dosage reductions may be needed if adverse effects occur during therapy. Patients should be advised to notify their physician if they experience signs and symptoms of hepatotoxicity such as fever, rash, anorexia, nausea, vomiting, fatigue, right upper quadrant pain, dark urine, and jaundice. In addition, they should seek medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, palpitations, or syncope. Following discontinuation of the potent CYP450 3A4 inhibitor, a washout period of approximately one week should be allowed before the pazopanib dosage is adjusted upward.', 'Metabolism', 'Concomitant use of pazopanib with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358296, 'Pemigatinib', 'Atazanavir', 'Major', 'Coadministration with potent or moderate inhibitors of CYP450 3A4 may increase the plasma concentrations of pemigatinib, which is primarily metabolized by the isoenzyme in vitro.', 'DDInter', 1767317475, 'Concomitant use of pemigatinib with potent or moderate CYP450 3A4 inhibitors should be avoided. If coadministration is required, a reduction in the dosage of pemigatinib is recommended.', 'Metabolism', 'Concomitant use of pemigatinib with potent or moderate CYP450 3A4 inhibitors should be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358297, 'Penbutolol', 'Atazanavir', 'Major', 'Atazanavir has been shown to prolong the PR interval of the electrocardiogram in some patients. Theoretically, coadministration with other agents that prolong the PR interval (e.g., beta blockers, digoxin, lacosamide, mefloquine, verapamil) may result in elevated risk of conduction disturbances and atrioventricular block.', 'DDInter', 1767317475, 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.g., verapamil), since atazanavir is an inhibitor of that isoenzyme.', 'Synergism', 'Caution is advised if atazanavir is used concomitantly with other agents that prolong the PR interval, particularly those that are metabolized by CYP450 3A4 (e.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358298, 'Pentobarbital', 'Atazanavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358299, 'Pexidartinib', 'Atazanavir', 'Major', 'Coadministration of pexidartinib with strong CYP450 3A4 inhibitors and/or uridine diphosphate glucuronosyltransferase (UGT) inhibitors may significantly increase the plasma concentrations and the incidence and severity of adverse effects of pexidartinib, including potentially fatal hepatotoxicity. The proposed mechanism is inhibition of CYP450 3A4 and/or UGT, the primary isoenzymes responsible for the metabolic clearance of pexidartinib.', 'DDInter', 1767317475, 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided. If concomitant use is required, the dose of pexidartinib should be reduced according to the manufacturer''s recommendations. If concomitant use of a strong CYP450 3A4 inhibitor or UGT inhibitor is discontinued, the dose of pexidartinib may be increased, after 3 plasma half-lives of the strong CYP450 3A4 inhibitor or UGT inhibitor, to the dose that was used prior to starting the strong CYP450 3A4 inhibitor or UGT inhibitor.', 'Metabolism', 'The use of pexidartinib with strong CYP450 3A4 inhibitors and/or UGT inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358300, 'Phenobarbital', 'Atazanavir', 'Moderate', 'Coadministration with drugs that are inducers of CYP450 3A4 may decrease the plasma concentrations of protease inhibitors (PIs), which are primarily metabolized by the isoenzyme.', 'DDInter', 1767317475, 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen. Coadministration of atazanavir without ritonavir and carbamazepine, phenobarbital, or phenytoin is not recommended. Antiretroviral response should be monitored more closely whenever a CYP450 3A4 inducer is added to or withdrawn from therapy, and the antiretroviral regimen adjusted as necessary.', 'Metabolism', 'Given the risk of reduced viral susceptibility and resistance development associated with subtherapeutic antiretroviral drug levels, protease inhibitors should be used cautiously with agents that induce CYP450 3A4, particularly if only one PI is used in the antiretroviral regimen.', NULL, NULL, 'Moderate', 'pharmacodynamic');
