INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, source, updated_at, management_text, mechanism_text, recommendation, arabic_recommendation, arabic_effect, risk_level, type) VALUES (1358472, 'Erythromycin', 'Cobicistat', 'Major', 'Coadministration with potent inhibitors of CYP450 3A4 may increase the plasma concentrations of erythromycin, which is primarily metabolized by the isoenzyme. The use of erythromycin has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes. In addition, erythromycin may increase the plasma concentrations of drugs that are substrates of CYP450 3A4.', 'DDInter', 1767317475, 'Concomitant use of erythromycin with potent CYP450 3A4 inhibitors should generally be avoided.', 'Metabolism', 'Concomitant use of erythromycin with potent CYP450 3A4 inhibitors should generally be avoided.', NULL, NULL, 'Major', 'pharmacodynamic');
