INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57552, 'Carboplatin', 'Tofacitinib', 'Major', 'Coadministration of tofacitinib with other immuno- or myelosuppressive agents may potentiate the risk of infections as well as lymphoma and other malignancies. Serious and sometimes fatal infections due to bacterial, mycobacterial, invasive fungal, viral, or other opportunistic pathogens have been reported in patients receiving tofacitinib, most of whom were taking concomitant immunosuppressants such as methotrexate or corticosteroids. Lymphoma and other malignancies have also been observed with tofacitinib use, with or without concomitant immunosuppressants.', NULL, 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy.', NULL, 'Close monitoring for the development of infection is recommended if tofacitinib is used in combination with other immuno- or myelosuppressive agents, including the possible development of tuberculosis in patients who tested negative for latent tuberculosis infection prior to initiating therapy. Lymphocyte and neutrophil counts as well as hemoglobin should be evaluated at baseline and regularly during therapy, and tofacitinib dosage adjusted as necessary in accordance with the product labeling. Patients should be advised to contact their physician if they develop signs and symptoms of infection such as fever, chills, diarrhea, sore throat, muscle aches, shortness of breath, blood in phlegm, weight loss, red or inflamed skin, body sores, and pain or burning during urination. If a serious infection, an opportunistic infection, or sepsis develops, tofacitinib should be interrupted until the infection is controlled.', 'Others', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57628/', '[1] "Product Information. Xeljanz (tofacitinib)." Pfizer U.S. Pharmaceuticals Group, New York, NY.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57553, 'Carboplatin', 'Topotecan', 'Major', 'Topotecan combined with a platinum agent may produce synergistic cytotoxicity against some germ cell cancers. However, myelosuppression may also be more severe.', NULL, 'There are currently insufficient data to recommend a safe and effective regimen for topotecan in combination with a platinum agent.', NULL, 'There are currently insufficient data to recommend a safe and effective regimen for topotecan in combination with a platinum agent. Clinicians should recognize the potential risk for severe neutropenia if these agents are used together, and bone marrow function must be closely monitored. Lower dosages of each agent may be required.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57629/', '[1] Chou TC, Motzer RJ, Tong Y, Bosl GJ "Computerized quantitation of synergism and antagonism of taxol, topotecan, and cisplatin against human teratocarcinoma cell growth: a rational approach to clinical protocol design." J Natl Cancer Inst 86 (1994): 1517-24[2] "Product Information. Hycamtin (topotecan)." SmithKline Beecham, Philadelphia, PA.[3] Rowinsky E, Grochow L, Kaufmann S, Bowling K, Sartorius S, Chen T, Lubejko B, Ettinger D, Peereboom D, Donehower R "Sequence-dependent effects of topotecan (T) and cisplatin (C) in a Phase I and pharmacokinetic (PK) study (Meeting abstract)." Proc Annu Meet Am Soc Clin Oncol 13 (1994): a3611994', 1767365204);
