INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57684, 'Dofetilide', 'Diltiazem', 'Moderate', 'Coadministration with inhibitors of CYP450 3A4 may increase the plasma concentrations of dofetilide, which is partially metabolized by the isoenzyme. The use of dofetilide has been associated with dose-related prolongation of the QT interval, thus elevated plasma levels of the drug may potentiate the risk of ventricular arrhythmias such as ventricular tachycardia and torsade de pointes as well as cardiac arrest and sudden death.', NULL, 'Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage of dofetilide adjusted accordingly as necessary.', NULL, 'Close clinical and laboratory monitoring is recommended whenever a CYP450 3A4 inhibitor is added to or withdrawn from therapy, and the dosage of dofetilide adjusted accordingly as necessary. Patients should be advised to seek prompt medical attention if they experience symptoms that could indicate the occurrence of torsade de pointes such as dizziness, lightheadedness, fainting, palpitation, irregular heart rhythm, shortness of breath, or syncope.', 'Metabolism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57760/', '[1] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.[2] "Product Information. Tikosyn (dofetilide)" Pfizer US Pharmaceuticals, Atlanta, GA.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57685, 'Dolasetron', 'Diltiazem', 'Major', 'Dolasetron has been shown to cause dose-dependent prolongation of the PR and QRS intervals. There have been reports of second- and third-degree atrioventricular block, cardiac arrest, and serious ventricular arrhythmias including fatalities in both adult and pediatric patients.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors.', NULL, 'Caution is advised if dolasetron is used concomitantly with other agents that prolong the PR or QRS interval, especially in the elderly and patients with known risk factors. An ECG should be obtained in these patients before initiating dolasetron and periodically during treatment. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat. Dolasetron should be avoided in patients with complete heart block or at risk for complete heart block, unless they have an implanted pacemaker.', 'Synergism', NULL, NULL, 'Major', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57761/', '[1] "Product Information. Anzemet (dolasetron)." Hoechst Marion-Roussel Inc, Kansas City, MO.', 1767365204);
INSERT OR IGNORE INTO drug_interactions (id, ingredient1, ingredient2, severity, effect, arabic_effect, recommendation, arabic_recommendation, management_text, mechanism_text, alternatives_a, alternatives_b, risk_level, ddinter_id, source, type, metabolism_info, source_url, reference_text, updated_at) VALUES (57686, 'Donepezil', 'Diltiazem', 'Moderate', 'Since acetylcholinesterase inhibitors can cause bradycardia and heart block due to vagotonic effects on the sinoatrial and atrioventricular nodes, additive effects may occur with other agents that also possess bradycardic effects such as beta-blockers, calcium channel blockers, digitalis, some protease inhibitors (atazanavir, lopinavir-ritonavir, saquinavir), amiodarone, dronedarone, moricizine, lacosamide, and mefloquine.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs.', NULL, 'Caution is advised if acetylcholinesterase inhibitors are used concomitantly with bradycardic drugs. Patients with underlying structural heart disease, preexisting conduction system abnormalities, ischemic heart disease, or cardiomyopathies may be at increased risk for developing cardiac conduction disturbances and atrioventricular block. Patients should be advised to notify their physician if they experience dizziness, lightheadedness, fainting, or irregular heartbeat.', 'Synergism', NULL, NULL, 'Moderate', NULL, 'DDInter', 'pharmacodynamic', NULL, 'https://ddinter2.scbdd.com/server/interact/57762/', '[1] "Product Information. Razadyne (galantamine)." Johnson and Johnson Medical Inc, Arlington, TX.[2] "Product Information. Cognex (tacrine)." Parke-Davis, Morris Plains, NJ.[3] Tiseo PJ, Perdomo CA, Friedhoff LT "Concurrent administration of donepezil HCI and digoxin: assessment of pharmacokinetic changes." Br J Clin Pharmacol 46(Suppl 1) (1998): 40-4[4] Tavassoli N, Sommet A, Lapeyre-Mestre M, Bagheri H, Montrastruc JL "Drug interactions with cholinesterase inhibitors : an analysi